use rustler::{Atom, Encoder, Env, Error, NifResult, Term};
use std::collections::HashMap;
use std::sync::Arc;
use tokio::runtime::Runtime;
use uuid::Uuid;

mod sandbox_manager;
mod hvm_executor;
mod compute_backend;

use sandbox_manager::SandboxManager;
use hvm_executor::HvmExecutor;
use compute_backend::{ComputeBackend, ComputeMode};

// Atoms for Elixir
rustler::atoms! {
    ok,
    error,
    created,
    running,
    stopped,
    cuda,
    wasm,
    cpu,
    auto
}

// Initialize the NIF module
rustler::init!(
    "Elixir.Lance.Sandbox.ManifoldeProxydBridge",
    [
        create_sandbox,
        destroy_sandbox,
        execute_hvm,
        set_compute_mode,
        get_sandbox_stats,
        list_sandboxes
    ]
);

/// Create a new sandbox with manifolded isolation
#[rustler::nif]
fn create_sandbox(
    env: Env,
    image: String,
    memory_limit: u64,
    cpu_limit: f64,
) -> NifResult<Term> {
    let runtime = Runtime::new().map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let result = runtime.block_on(async {
        SandboxManager::create_sandbox(image, memory_limit, cpu_limit).await
    });

    match result {
        Ok(sandbox_id) => Ok((ok(), sandbox_id.to_string()).encode(env)),
        Err(e) => Ok((error(), e.to_string()).encode(env)),
    }
}

/// Destroy a sandbox
#[rustler::nif]
fn destroy_sandbox(env: Env, sandbox_id: String) -> NifResult<Term> {
    let id = Uuid::parse_str(&sandbox_id)
        .map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let runtime = Runtime::new().map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let result = runtime.block_on(async {
        SandboxManager::destroy_sandbox(id).await
    });

    match result {
        Ok(_) => Ok((ok(), "destroyed").encode(env)),
        Err(e) => Ok((error(), e.to_string()).encode(env)),
    }
}

/// Execute HVM code in a sandbox with automatic backend selection
#[rustler::nif(schedule = "DirtyCpu")]
fn execute_hvm(
    env: Env,
    sandbox_id: String,
    hvm_code: String,
    compute_mode: String,
) -> NifResult<Term> {
    let id = Uuid::parse_str(&sandbox_id)
        .map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let mode = match compute_mode.as_str() {
        "cuda" => ComputeMode::Cuda,
        "wasm" => ComputeMode::Wasm,
        "cpu" => ComputeMode::Cpu,
        "auto" => ComputeMode::Auto,
        _ => ComputeMode::Auto,
    };

    let runtime = Runtime::new().map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let result = runtime.block_on(async {
        HvmExecutor::execute_in_sandbox(id, hvm_code, mode).await
    });

    match result {
        Ok(output) => Ok((ok(), output).encode(env)),
        Err(e) => Ok((error(), e.to_string()).encode(env)),
    }
}

/// Set the compute mode for a sandbox
#[rustler::nif]
fn set_compute_mode(env: Env, sandbox_id: String, mode: String) -> NifResult<Term> {
    let id = Uuid::parse_str(&sandbox_id)
        .map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let compute_mode = match mode.as_str() {
        "cuda" => ComputeMode::Cuda,
        "wasm" => ComputeMode::Wasm,
        "cpu" => ComputeMode::Cpu,
        "auto" => ComputeMode::Auto,
        _ => return Ok((error(), "invalid_mode").encode(env)),
    };

    let runtime = Runtime::new().map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let result = runtime.block_on(async {
        SandboxManager::set_compute_mode(id, compute_mode).await
    });

    match result {
        Ok(_) => Ok((ok(), mode).encode(env)),
        Err(e) => Ok((error(), e.to_string()).encode(env)),
    }
}

/// Get statistics for a sandbox
#[rustler::nif]
fn get_sandbox_stats(env: Env, sandbox_id: String) -> NifResult<Term> {
    let id = Uuid::parse_str(&sandbox_id)
        .map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let runtime = Runtime::new().map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let result = runtime.block_on(async {
        SandboxManager::get_sandbox_stats(id).await
    });

    match result {
        Ok(stats) => {
            let stats_map = vec![
                ("memory_usage", stats.memory_usage),
                ("cpu_usage", stats.cpu_usage as u64),
                ("compute_mode", match stats.compute_mode {
                    ComputeMode::Cuda => 1,
                    ComputeMode::Wasm => 2,
                    ComputeMode::Cpu => 3,
                    ComputeMode::Auto => 0,
                }),
            ];
            Ok((ok(), stats_map).encode(env))
        }
        Err(e) => Ok((error(), e.to_string()).encode(env)),
    }
}

/// List all active sandboxes
#[rustler::nif]
fn list_sandboxes(env: Env) -> NifResult<Term> {
    let runtime = Runtime::new().map_err(|e| Error::Term(Box::new(e.to_string())))?;

    let result = runtime.block_on(async {
        SandboxManager::list_sandboxes().await
    });

    match result {
        Ok(sandboxes) => {
            let sandbox_list: Vec<(String, String)> = sandboxes
                .into_iter()
                .map(|(id, status)| (id.to_string(), format!("{:?}", status)))
                .collect();
            Ok((ok(), sandbox_list).encode(env))
        }
        Err(e) => Ok((error(), e.to_string()).encode(env)),
    }
}

defmodule CryptoboxPhoenix.SandboxManager do
  @moduledoc """
  High-level sandbox management interface.
  Provides a safe, supervised interface to sandbox operations.
  """

  use GenServer
  require Logger

  alias CryptoboxPhoenix.Native.{Sandbox, SandboxConfig, SandboxInfo}

  # Client API

  @doc """
  Start the sandbox manager.
  """
  def start_link(opts \\ []) do
    GenServer.start_link(__MODULE__, opts, name: __MODULE__)
  end

  @doc """
  Create a new sandbox with the given configuration.
  """
  @spec create_sandbox(map()) :: {:ok, String.t()} | {:error, term()}
  def create_sandbox(config) do
    GenServer.call(__MODULE__, {:create_sandbox, config}, 30_000)
  end

  @doc """
  Start an existing sandbox.
  """
  @spec start_sandbox(String.t()) :: :ok | {:error, term()}
  def start_sandbox(sandbox_id) do
    GenServer.call(__MODULE__, {:start_sandbox, sandbox_id})
  end

  @doc """
  Stop a running sandbox.
  """
  @spec stop_sandbox(String.t()) :: :ok | {:error, term()}
  def stop_sandbox(sandbox_id) do
    GenServer.call(__MODULE__, {:stop_sandbox, sandbox_id})
  end

  @doc """
  Destroy a sandbox completely.
  """
  @spec destroy_sandbox(String.t()) :: :ok | {:error, term()}
  def destroy_sandbox(sandbox_id) do
    GenServer.call(__MODULE__, {:destroy_sandbox, sandbox_id})
  end

  @doc """
  Execute a command in a sandbox.
  """
  @spec exec_in_sandbox(String.t(), String.t(), list(String.t())) :: 
    {:ok, String.t()} | {:error, term()}
  def exec_in_sandbox(sandbox_id, command, args \\ []) do
    GenServer.call(__MODULE__, {:exec_in_sandbox, sandbox_id, command, args}, 60_000)
  end

  @doc """
  Get information about a sandbox.
  """
  @spec get_sandbox_info(String.t()) :: {:ok, SandboxInfo.t()} | {:error, term()}
  def get_sandbox_info(sandbox_id) do
    GenServer.call(__MODULE__, {:get_sandbox_info, sandbox_id})
  end

  @doc """
  List all sandboxes.
  """
  @spec list_sandboxes() :: {:ok, list()} | {:error, term()}
  def list_sandboxes do
    GenServer.call(__MODULE__, :list_sandboxes)
  end

  @doc """
  Set resource limits for a sandbox.
  """
  @spec set_resource_limits(String.t(), non_neg_integer(), float()) :: 
    :ok | {:error, term()}
  def set_resource_limits(sandbox_id, memory_limit, cpu_limit) do
    GenServer.call(__MODULE__, {:set_resource_limits, sandbox_id, memory_limit, cpu_limit})
  end

  @doc """
  Enable or disable network isolation for a sandbox.
  """
  @spec isolate_network(String.t(), boolean()) :: :ok | {:error, term()}
  def isolate_network(sandbox_id, enable) do
    GenServer.call(__MODULE__, {:isolate_network, sandbox_id, enable})
  end

  # Server callbacks

  @impl true
  def init(opts) do
    # Start monitoring sandboxes
    Process.send_after(self(), :monitor_sandboxes, 5_000)

