//! Manifolded Deno - WASM Distribution & Edge Deployment
//! 
//! This crate provides Deno runtime integration for deploying WASM modules
//! to edge locations globally, enabling distributed compute at the edge.

use anyhow::{Result, Context};
use async_trait::async_trait;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::PathBuf;
use uuid::Uuid;

pub mod compiler;
pub mod deployer;
pub mod edge_runtime;
pub mod optimizer;

/// Configuration for Deno deployment
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DenoDeployConfig {
    /// Project name for Deno Deploy
    pub project_name: String,
    /// API token for authentication
    pub api_token: String,
    /// Target regions for edge deployment
    pub regions: Vec<EdgeRegion>,
    /// Auto-scaling configuration
    pub auto_scaling: AutoScalingConfig,
    /// Cost optimization settings
    pub cost_optimization: CostOptimizationConfig,
}

/// Edge region for deployment
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum EdgeRegion {
    Global,
    NorthAmerica,
    Europe,
    AsiaPacific,
    SouthAmerica,
    Africa,
    Custom(String),
}

/// Auto-scaling configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AutoScalingConfig {
    pub min_instances: u32,
    pub max_instances: u32,
    pub target_cpu_utilization: f64,
    pub target_memory_utilization: f64,
    pub scale_up_threshold: f64,
    pub scale_down_threshold: f64,
}

/// Cost optimization configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CostOptimizationConfig {
    pub max_cost_per_hour: f64,
    pub preferred_regions: Vec<EdgeRegion>,
    pub spot_instances_enabled: bool,
    pub idle_timeout_minutes: u32,
}

/// WASM module prepared for Deno deployment
#[derive(Debug, Clone)]
pub struct WasmModule {
    pub id: Uuid,
    pub name: String,
    pub bytecode: Vec<u8>,
    pub metadata: ModuleMetadata,
    pub optimizations: OptimizationMetrics,
}

/// Module metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ModuleMetadata {
    pub entry_point: String,
    pub exports: Vec<String>,
    pub imports: Vec<String>,
    pub memory_requirements: MemoryRequirements,
    pub compute_requirements: ComputeRequirements,
}

/// Memory requirements for the module
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MemoryRequirements {
    pub min_memory_mb: u32,
    pub max_memory_mb: u32,
    pub heap_size_mb: u32,
    pub stack_size_mb: u32,
}

/// Compute requirements for the module
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ComputeRequirements {
    pub min_cpu_cores: f32,
    pub gpu_required: bool,
    pub gpu_memory_gb: Option<u32>,
    pub estimated_execution_time_ms: u64,
}

/// Optimization metrics after compilation
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct OptimizationMetrics {
    pub original_size_bytes: usize,
    pub optimized_size_bytes: usize,
    pub compression_ratio: f64,
    pub estimated_execution_speedup: f64,
    pub optimizations_applied: Vec<String>,
}

/// Deployment result from Deno Deploy
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DeploymentResult {
    pub deployment_id: Uuid,
    pub project_url: String,
    pub edge_locations: Vec<EdgeLocation>,
    pub estimated_cost_per_hour: f64,
    pub performance_metrics: PerformanceMetrics,
}

/// Edge location where the module is deployed
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EdgeLocation {
    pub region: EdgeRegion,
    pub city: String,
    pub latency_ms: f64,
    pub availability_zone: String,
}

/// Performance metrics for deployment
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PerformanceMetrics {
    pub cold_start_time_ms: f64,
    pub warm_start_time_ms: f64,
    pub p50_latency_ms: f64,
    pub p99_latency_ms: f64,
    pub throughput_rps: f64,
}

/// Main Deno runtime manager
pub struct DenoRuntimeManager {
    config: DenoDeployConfig,
    deployed_modules: HashMap<Uuid, DeploymentResult>,
}

impl DenoRuntimeManager {
    /// Create a new Deno runtime manager
    pub fn new(config: DenoDeployConfig) -> Self {
        Self {
            config,
            deployed_modules: HashMap::new(),
        }
    }

    /// Compile Rust/TypeScript to optimized WASM
    pub async fn compile_to_wasm(&self, source_path: PathBuf) -> Result<WasmModule> {
        compiler::compile_to_wasm(source_path, &self.config).await
    }

    /// Deploy WASM module to Deno Deploy edge locations
    pub async fn deploy_module(&mut self, module: WasmModule) -> Result<DeploymentResult> {
        let result = deployer::deploy_to_edge(module, &self.config).await?;
        self.deployed_modules.insert(result.deployment_id, result.clone());
        Ok(result)
    }

    /// Distribute compute workload across edge locations
    pub async fn distribute_compute(
        &self,
        workload: ComputeWorkload,
        locations: Vec<EdgeRegion>,
    ) -> Result<DistributionResult> {
        edge_runtime::distribute_workload(workload, locations, &self.config).await
    }

    /// Monitor deployed modules
    pub async fn monitor_deployments(&self) -> Result<Vec<DeploymentStatus>> {
        let mut statuses = Vec::new();
        for (id, deployment) in &self.deployed_modules {
            let status = self.get_deployment_status(*id).await?;
            statuses.push(status);
        }
        Ok(statuses)
    }

    /// Get deployment status
    async fn get_deployment_status(&self, deployment_id: Uuid) -> Result<DeploymentStatus> {
        deployer::get_deployment_status(deployment_id, &self.config).await
    }

    /// Scale deployment based on metrics
    pub async fn scale_deployment(
        &self,
        deployment_id: Uuid,
        scale_config: ScaleConfig,
    ) -> Result<()> {
        deployer::scale_deployment(deployment_id, scale_config, &self.config).await
    }

    /// Optimize deployment for cost
    pub async fn optimize_for_cost(&self, deployment_id: Uuid) -> Result<OptimizationResult> {
        optimizer::optimize_deployment_cost(deployment_id, &self.config).await
    }
}

/// Compute workload to distribute
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ComputeWorkload {
    pub id: Uuid,
    pub name: String,
    pub tasks: Vec<WorkloadTask>,
    pub priority: WorkloadPriority,
    pub deadline: Option<chrono::DateTime<chrono::Utc>>,
}

/// Individual task in a workload
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WorkloadTask {
    pub id: Uuid,
    pub function_name: String,
    pub input_data: serde_json::Value,
    pub expected_duration_ms: u64,
    pub memory_required_mb: u32,
}

/// Workload priority
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum WorkloadPriority {
    Low,
    Normal,
    High,
    Critical,
}

/// Result of workload distribution
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DistributionResult {
    pub workload_id: Uuid,
    pub task_assignments: HashMap<Uuid, EdgeLocation>,
    pub estimated_completion_time: chrono::DateTime<chrono::Utc>,
    pub estimated_cost: f64,
}

/// Deployment status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DeploymentStatus {
    pub deployment_id: Uuid,
    pub status: DeploymentState,
    pub health: HealthStatus,
    pub metrics: RuntimeMetrics,
}

/// Deployment state
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum DeploymentState {
    Deploying,
    Running,
    Scaling,
    Stopped,
    Failed(String),
}

/// Health status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum HealthStatus {
    Healthy,
    Degraded,
    Unhealthy,
}

/// Runtime metrics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RuntimeMetrics {
    pub requests_per_second: f64,
    pub error_rate: f64,
    pub cpu_utilization: f64,
    pub memory_utilization: f64,
    pub active_instances: u32,
}

/// Scale configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ScaleConfig {
    pub target_instances: u32,
    pub regions: Vec<EdgeRegion>,
}

/// Optimization result
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct OptimizationResult {
    pub previous_cost: f64,
    pub optimized_cost: f64,
    pub savings_percentage: f64,
    pub recommendations: Vec<String>,
}

// Module implementations
pub mod compiler {
    use super::*;
    use std::path::PathBuf;

    pub async fn compile_to_wasm(
        source_path: PathBuf,
        config: &DenoDeployConfig,
    ) -> Result<WasmModule> {
