//! Manifolded Aethir - Decentralized Compute Integration
//! 
//! This crate provides integration with Aethir's decentralized compute network,
//! enabling dynamic resource scaling with tokenomics-based incentives.

use anyhow::{Result, Context};
use async_trait::async_trait;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;
use tokio::sync::RwLock;
use uuid::Uuid;
use rust_decimal::Decimal;
use chrono::{DateTime, Utc};

pub mod tokenomics;
pub mod marketplace;
pub mod reputation;
pub mod payment;
pub mod discovery;
pub mod orchestrator;

/// Aethir compute provider configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AethirConfig {
    /// Network endpoint for Aethir
    pub network_endpoint: String,
    /// Wallet address for payments
    pub wallet_address: String,
    /// Private key for signing (encrypted)
    pub encrypted_private_key: Vec<u8>,
    /// Token contract address
    pub token_contract: String,
    /// Minimum reputation score for providers
    pub min_reputation_score: f64,
    /// Maximum cost per compute hour
    pub max_cost_per_hour: Decimal,
    /// Preferred regions
    pub preferred_regions: Vec<String>,
}

/// Compute requirements for a workload
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ComputeRequirements {
    /// Number of CPU cores needed
    pub cpu_cores: u32,
    /// Memory in GB
    pub memory_gb: u32,
    /// GPU requirements
    pub gpu: Option<GpuRequirements>,
    /// Storage in GB
    pub storage_gb: u32,
    /// Network bandwidth in Mbps
    pub bandwidth_mbps: u32,
    /// Duration in hours
    pub duration_hours: f64,
    /// Geographic preferences
    pub geo_preferences: Option<GeoPreferences>,
}

/// GPU requirements
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GpuRequirements {
    /// Number of GPUs
    pub count: u32,
    /// Minimum VRAM per GPU in GB
    pub vram_gb: u32,
    /// GPU model preferences
    pub models: Vec<String>,
    /// CUDA compute capability
    pub cuda_capability: Option<String>,
}

/// Geographic preferences for compute
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GeoPreferences {
    /// Preferred regions
    pub preferred_regions: Vec<String>,
    /// Maximum latency in ms
    pub max_latency_ms: u32,
    /// Data sovereignty requirements
    pub data_sovereignty: Option<String>,
}

/// Budget constraints for compute
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Budget {
    /// Maximum total cost
    pub max_total_cost: Decimal,
    /// Maximum cost per hour
    pub max_hourly_cost: Decimal,
    /// Currency (ATH tokens or USD equivalent)
    pub currency: Currency,
    /// Payment method
    pub payment_method: PaymentMethod,
}

/// Currency for payments
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum Currency {
    ATH,  // Aethir tokens
    USD,  // USD equivalent
    ETH,  // Ethereum
    BTC,  // Bitcoin
}

/// Payment method
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum PaymentMethod {
    Direct,           // Direct token transfer
    PaymentChannel,   // Lightning-style payment channels
    Escrow,          // Smart contract escrow
    Subscription,    // Pre-paid subscription
}

/// Compute allocation from Aethir network
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ComputeAllocation {
    pub allocation_id: Uuid,
    pub provider: Provider,
    pub resources: AllocatedResources,
    pub pricing: PricingDetails,
    pub contract: SmartContract,
    pub status: AllocationStatus,
}

/// Provider information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Provider {
    pub provider_id: String,
    pub name: String,
    pub reputation_score: f64,
    pub location: String,
    pub capabilities: ProviderCapabilities,
    pub availability: f64,
}

/// Provider capabilities
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ProviderCapabilities {
    pub total_cpu_cores: u32,
    pub total_memory_gb: u32,
    pub total_gpus: u32,
    pub gpu_models: Vec<String>,
    pub network_bandwidth_gbps: f64,
    pub specialized_hardware: Vec<String>,
}

/// Allocated resources
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AllocatedResources {
    pub cpu_cores: u32,
    pub memory_gb: u32,
    pub gpus: Vec<GpuAllocation>,
    pub storage_gb: u32,
    pub bandwidth_mbps: u32,
    pub ip_addresses: Vec<String>,
}

/// GPU allocation details
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GpuAllocation {
    pub gpu_id: String,
    pub model: String,
    pub vram_gb: u32,
    pub cuda_cores: u32,
}

/// Pricing details for allocation
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PricingDetails {
    pub base_cost_per_hour: Decimal,
    pub gpu_premium: Decimal,
    pub bandwidth_cost: Decimal,
    pub storage_cost: Decimal,
    pub total_cost_per_hour: Decimal,
    pub discount_percentage: f64,
}

/// Smart contract for compute agreement
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SmartContract {
    pub contract_address: String,
    pub creation_time: DateTime<Utc>,
    pub expiration_time: DateTime<Utc>,
    pub terms: ContractTerms,
    pub escrow_amount: Decimal,
}

/// Contract terms
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContractTerms {
    pub sla_uptime: f64,
    pub penalty_rate: Decimal,
    pub early_termination_fee: Decimal,
    pub auto_renewal: bool,
}

/// Allocation status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum AllocationStatus {
    Pending,
    Active,
    Suspended,
    Terminated,
    Failed(String),
}

/// Compute offer from a provider
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ComputeOffer {
    pub offer_id: Uuid,
    pub provider: Provider,
    pub available_resources: ProviderCapabilities,
    pub pricing: PricingDetails,
    pub availability_window: AvailabilityWindow,
}

/// Availability window
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AvailabilityWindow {
    pub start_time: DateTime<Utc>,
    pub end_time: DateTime<Utc>,
    pub guaranteed_duration_hours: f64,
}

/// Bid on compute resources
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Bid {
    pub bid_id: Uuid,
    pub offer_id: Uuid,
    pub bidder_address: String,
    pub bid_amount: Decimal,
    pub requirements: ComputeRequirements,
    pub status: BidStatus,
}

/// Bid status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum BidStatus {
    Submitted,
    Accepted,
    Rejected,
    Countered(Decimal),
    Expired,
}

/// Main Aethir compute provider
pub struct AethirComputeProvider {
    config: AethirConfig,
    tokenomics_engine: Arc<tokenomics::TokenomicsEngine>,
    compute_marketplace: Arc<marketplace::ComputeMarketplace>,
    reputation_system: Arc<reputation::ReputationTracker>,
    payment_manager: Arc<payment::PaymentChannelManager>,
    active_allocations: Arc<RwLock<HashMap<Uuid, ComputeAllocation>>>,
}

impl AethirComputeProvider {
    /// Create a new Aethir compute provider
    pub async fn new(config: AethirConfig) -> Result<Self> {
        let tokenomics_engine = Arc::new(tokenomics::TokenomicsEngine::new(&config).await?);
        let compute_marketplace = Arc::new(marketplace::ComputeMarketplace::new(&config).await?);
        let reputation_system = Arc::new(reputation::ReputationTracker::new(&config).await?);
        let payment_manager = Arc::new(payment::PaymentChannelManager::new(&config).await?);

        Ok(Self {
            config,
            tokenomics_engine,
            compute_marketplace,
            reputation_system,
            payment_manager,
            active_allocations: Arc::new(RwLock::new(HashMap::new())),
        })
    }

    /// Request compute resources
    pub async fn request_compute(
        &self,
        requirements: ComputeRequirements,
        budget: Budget,
    ) -> Result<ComputeAllocation> {
        // Find suitable providers
        let offers = self.compute_marketplace
            .find_offers(&requirements, &budget)
            .await?;

        // Filter by reputation
        let reputable_offers = self.filter_by_reputation(offers).await?;

        // Select optimal offer based on tokenomics
        let optimal_offer = self.tokenomics_engine
            .select_optimal_offer(reputable_offers, &budget)
            .await?;

        // Create bid
        let bid = self.create_bid(optimal_offer, requirements, budget).await?;

        // Submit bid and wait for acceptance
        let allocation = self.submit_and_await_bid(bid).await?;

        // Store allocation
        let mut allocations = self.active_allocations.write().await;
        allocations.insert(allocation.allocation_id, allocation.clone());

        Ok(allocation)
    }

    /// Bid on specific compute offer
    pub async fn bid_on_compute(
        &self,
        provider_id: String,
        offer: ComputeOffer,
    ) -> Result<Bid> {
        // Verify provider reputation
        let reputation = self.reputation_system
            .get_reputation(&provider_id)
            .await?;

        if reputation < self.config.min_reputation_score {
            return Err(anyhow::anyhow!("Provider reputation too low"));
        }

        // Calculate bid amount based on tokenomics
        let bid_amount = self.tokenomics_engine
            .calculate_bid_amount(&offer)
            .await?;

        // Create and submit bid
        let bid = Bid {
            bid_id: Uuid::new_v4(),
            offer_id: offer.offer_id,
            bidder_address: self.config.wallet_address.clone(),
            bid_amount,
            requirements: self.extract_requirements(&offer),
            status: BidStatus::Submitted,
        };

        // Submit to marketplace
        self.compute_marketplace.submit_bid(&bid).await?;

        Ok(bid)
    }

    /// Monitor active allocations
    pub async fn monitor_allocations(&self) -> Result<Vec<AllocationMetrics>> {
        let allocations = self.active_allocations.read().await;
        let mut metrics = Vec::new();

        for (id, allocation) in allocations.iter() {
            let metric = self.get_allocation_metrics(allocation).await?;
            metrics.push(metric);
        }

        Ok(metrics)
    }

    /// Scale allocation up or down
    pub async fn scale_allocation(
        &self,
        allocation_id: Uuid,
        new_requirements: ComputeRequirements,
    ) -> Result<ComputeAllocation> {
        let mut allocations = self.active_allocations.write().await;
        let allocation = allocations.get_mut(&allocation_id)
            .ok_or_else(|| anyhow::anyhow!("Allocation not found"))?;

        // Request scaling from provider
        let scaled_allocation = self.compute_marketplace
            .scale_allocation(allocation, new_requirements)
            .await?;

        // Update payment channels
        self.payment_manager
            .update_payment_for_scaling(&scaled_allocation)
            .await?;

        // Update stored allocation
        *allocation = scaled_allocation.clone();

        Ok(scaled_allocation)
    }

    /// Terminate allocation
    pub async fn terminate_allocation(&self, allocation_id: Uuid) -> Result<()> {
        let mut allocations = self.active_allocations.write().await;
        let allocation = allocations.remove(&allocation_id)
            .ok_or_else(|| anyhow::anyhow!("Allocation not found"))?;

        // Terminate on marketplace
        self.compute_marketplace
            .terminate_allocation(&allocation)
            .await?;

        // Settle payments
        self.payment_manager
            .settle_final_payment(&allocation)
            .await?;

        // Update reputation based on experience
        self.reputation_system
            .update_reputation(&allocation.provider.provider_id, 5.0)
            .await?;

        Ok(())
    }

    // Helper methods
    async fn filter_by_reputation(&self, offers: Vec<ComputeOffer>) -> Result<Vec<ComputeOffer>> {
        let mut reputable = Vec::new();
        for offer in offers {
            let reputation = self.reputation_system
                .get_reputation(&offer.provider.provider_id)
                .await?;
            if reputation >= self.config.min_reputation_score {
                reputable.push(offer);
            }
        }
        Ok(reputable)
    }

    async fn create_bid(
        &self,
        offer: ComputeOffer,
        requirements: ComputeRequirements,
        budget: Budget,
    ) -> Result<Bid> {
        let bid_amount = self.tokenomics_engine
            .calculate_bid_amount(&offer)
            .await?;

        Ok(Bid {
            bid_id: Uuid::new_v4(),
            offer_id: offer.offer_id,
            bidder_address: self.config.wallet_address.clone(),
            bid_amount,
            requirements,
            status: BidStatus::Submitted,
        })
    }

    async fn submit_and_await_bid(&self, bid: Bid) -> Result<ComputeAllocation> {
        self.compute_marketplace.submit_bid(&bid).await?;
        self.compute_marketplace.await_bid_acceptance(&bid).await
    }

    fn extract_requirements(&self, offer: &ComputeOffer) -> ComputeRequirements {
        ComputeRequirements {
            cpu_cores: offer.available_resources.total_cpu_cores,
            memory_gb: offer.available_resources.total_memory_gb,
            gpu: if offer.available_resources.total_gpus > 0 {
                Some(GpuRequirements {
                    count: offer.available_resources.total_gpus,
                    vram_gb: 8, // Default
                    models: offer.available_resources.gpu_models.clone(),
                    cuda_capability: None,
                })
            } else {
                None
            },
            storage_gb: 100, // Default
            bandwidth_mbps: 1000, // Default
            duration_hours: 1.0, // Default
            geo_preferences: None,
        }
    }

    async fn get_allocation_metrics(&self, allocation: &ComputeAllocation) -> Result<AllocationMetrics> {
        // Implementation would query actual metrics
        Ok(AllocationMetrics {
            allocation_id: allocation.allocation_id,
            cpu_utilization: 0.75,
            memory_utilization: 0.80,
            gpu_utilization: 0.90,
            network_throughput_mbps: 500.0,
            uptime_percentage: 99.9,
            cost_so_far: Decimal::new(1000, 2),
        })
    }
}

/// Allocation metrics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AllocationMetrics {
    pub allocation_id: Uuid,
    pub cpu_utilization: f64,
    pub memory_utilization: f64,
    pub gpu_utilization: f64,
    pub network_throughput_mbps: f64,
    pub uptime_percentage: f64,
    pub cost_so_far: Decimal,
}

