        if let Some(preferred) = &workload.preferred_backend {
            return preferred.clone();
        }

        match gpu.vendor {
            GpuVendor::Nvidia if self.cuda_available => ComputeBackend::CUDA,
            GpuVendor::AMD if self.rocm_available => ComputeBackend::ROCm,
            _ if self.opencl_available => ComputeBackend::OpenCL,
            _ => ComputeBackend::Vulkan,
        }
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GpuMetrics {
    pub gpu_id: u32,
    pub temperature_c: f32,
    pub power_watts: f32,
    pub memory_used_gb: f32,
    pub gpu_utilization: f32,
    pub memory_utilization: f32,
}

/// Cost optimizer for GPU allocation
pub struct CostOptimizer {
    pricing_models: HashMap<GpuVendor, PricingModel>,
}

#[derive(Debug, Clone)]
pub struct PricingModel {
    pub base_cost_per_hour: f64,
    pub memory_cost_per_gb: f64,
    pub compute_cost_per_tflop: f64,
}

impl CostOptimizer {
    pub fn optimize_allocation(
        &self,
        workload: &Workload,
        available_gpus: &[GpuInfo],
    ) -> Result<OptimalAllocation> {
        // Find most cost-effective GPU
        Ok(OptimalAllocation {
            gpu_id: 0,
            estimated_cost_per_hour: 1.0,
            performance_score: 0.9,
        })
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct OptimalAllocation {
    pub gpu_id: u32,
    pub estimated_cost_per_hour: f64,
    pub performance_score: f64,
}

