// Include phoenix_html to handle method=PUT/DELETE in forms and buttons.
import "phoenix_html"
// Establish Phoenix Socket and LiveView configuration.
import {Socket} from "phoenix"
import {LiveSocket} from "phoenix_live_view"
import topbar from "../vendor/topbar"

// Import React and ReactDOM
import React from "react"
import { createRoot } from "react-dom/client"

// Import our React components
import { KatalystAdvancedDashboard } from "./react/katalyst_dashboard"
import { ReactPhoenixBridge } from "./react/phoenix_bridge"

// Phoenix Socket setup
let csrfToken = document.querySelector("meta[name='csrf-token']").getAttribute("content")

// LiveView Hooks for React Integration
let Hooks = {}

// Katalyst Dashboard Hook - Mounts React component and manages Phoenix bridge
Hooks.KatalystDashboard = {
  mounted() {
    const container = this.el
    const props = {
      multithreadingConfig: JSON.parse(container.dataset.multithreadingConfig),
      systemMetrics: JSON.parse(container.dataset.systemMetrics),
      sessions: JSON.parse(container.dataset.sessions),
      activeSessionId: container.dataset.activeSessionId,
      websocketUrl: container.dataset.websocketUrl
    }

    // Initialize Phoenix bridge
    this.bridge = new ReactPhoenixBridge(this, props.websocketUrl)

    // Create React root and render dashboard
    this.root = createRoot(container)
    this.root.render(
      React.createElement(KatalystAdvancedDashboard, {
        ...props,
        bridge: this.bridge,
        onMount: () => {
          // Notify LiveView that React is ready
          this.pushEvent("react_mounted", {})
        },
        onWebSocketConnect: () => {
          // Notify LiveView of WebSocket connection
          this.pushEvent("websocket_connected", {})
        }
      })
    )

    // Handle LiveView events
    this.handleEvent("session_created", (payload) => {
      this.bridge.handleSessionCreated(payload.session)
    })

    this.handleEvent("session_closed", (payload) => {
      this.bridge.handleSessionClosed(payload.session_id)
    })

    this.handleEvent("session_selected", (payload) => {
      this.bridge.handleSessionSelected(payload.session_id)
    })

    console.log("Katalyst Dashboard Hook mounted successfully")
  },

  updated() {
    // Update React component props when LiveView updates
    const container = this.el
    const props = {
      multithreadingConfig: JSON.parse(container.dataset.multithreadingConfig),
      systemMetrics: JSON.parse(container.dataset.systemMetrics),
      sessions: JSON.parse(container.dataset.sessions),
      activeSessionId: container.dataset.activeSessionId,
      websocketUrl: container.dataset.websocketUrl
    }

    if (this.bridge) {
      this.bridge.updateProps(props)
    }
  },

  destroyed() {
    // Cleanup React component and Phoenix bridge
    if (this.root) {
      this.root.unmount()
    }
    if (this.bridge) {
      this.bridge.disconnect()
    }
    console.log("Katalyst Dashboard Hook destroyed")
  }
}

// LiveSocket configuration
let liveSocket = new LiveSocket("/live", Socket, {
  longPollFallbackMs: 2500,
  params: {_csrf_token: csrfToken},
  hooks: Hooks
})

// Show progress bar on live navigation and form submits
topbar.config({barColors: {0: "#29d"}, shadowColor: "rgba(0, 0, 0, .3)"})
window.addEventListener("phx:page-loading-start", _info => topbar.show(300))
window.addEventListener("phx:page-loading-stop", _info => topbar.hide())

