import { Socket } from "phoenix"

/**
 * ReactPhoenixBridge - Manages communication between React components and Phoenix LiveView/Channels
 * 
 * This bridge provides:
 * - WebSocket connections to Phoenix channels
 * - State synchronization between React and Phoenix
 * - Event handling and broadcasting
 * - Real-time updates and messaging
 */
export class ReactPhoenixBridge {
  constructor(liveViewHook, websocketUrl) {
    this.liveViewHook = liveViewHook
    this.websocketUrl = websocketUrl
    this.socket = null
    this.channels = new Map()
    this.callbacks = new Map()
    this.props = {}

    // Initialize WebSocket connection
    this.initSocket()
  }

  initSocket() {
    // Create Phoenix socket connection
    this.socket = new Socket(this.websocketUrl, {
      params: { 
        user_id: "react_client",
        session_id: this.generateSessionId()
      }
    })

    this.socket.connect()

    // Handle connection events
    this.socket.onOpen(() => {
      console.log("React-Phoenix bridge connected")
      this.joinChannels()
      this.notifyCallback('socket_connected')
    })

    this.socket.onError((error) => {
      console.error("React-Phoenix bridge error:", error)
      this.notifyCallback('socket_error', error)
    })

    this.socket.onClose(() => {
      console.log("React-Phoenix bridge disconnected")
      this.notifyCallback('socket_disconnected')
    })
  }

  joinChannels() {
    // Join Katalyst main channel
    this.joinChannel("katalyst:main", {
      onJoin: (response) => {
        console.log("Joined Katalyst channel:", response)
        this.notifyCallback('katalyst_connected', response)
      },
      onEvents: {
        "task:status_update": (payload) => this.notifyCallback('task_status_update', payload),
        "task:completed": (payload) => this.notifyCallback('task_completed', payload),
        "session:created": (payload) => this.notifyCallback('session_created', payload),
        "resources:updated": (payload) => this.notifyCallback('resources_updated', payload)
      }
    })

    // Join Terminal channel
    this.joinChannel("terminal:main", {
      onJoin: (response) => {
        console.log("Joined Terminal channel:", response)
      },
      onEvents: {
        "terminal:output": (payload) => this.notifyCallback('terminal_output', payload),
        "terminal:clear": (payload) => this.notifyCallback('terminal_clear', payload),
        "terminal:command_executed": (payload) => this.notifyCallback('terminal_command_executed', payload)
      }
    })

    // Join Resources channel
    this.joinChannel("resources:main", {
      onJoin: (response) => {
        console.log("Joined Resources channel:", response)
        // Start monitoring
        this.sendChannelMessage("resources:main", "monitoring:start", { interval: 1000 })
      },
      onEvents: {
        "resources:updated": (payload) => this.notifyCallback('resources_updated', payload)
      }
    })

    // Join AI Tasks channel
    this.joinChannel("ai_tasks:main", {
      onJoin: (response) => {
        console.log("Joined AI Tasks channel:", response)
      },
      onEvents: {
        "ai:task_started": (payload) => this.notifyCallback('ai_task_started', payload),
        "ai:task_progress": (payload) => this.notifyCallback('ai_task_progress', payload),
        "ai:task_completed": (payload) => this.notifyCallback('ai_task_completed', payload),
        "ai:task_cancelled": (payload) => this.notifyCallback('ai_task_cancelled', payload)
      }
    })
  }

  joinChannel(channelName, config) {
    const channel = this.socket.channel(channelName, {})

    channel.join()
      .receive("ok", (response) => {
        console.log(`Successfully joined ${channelName}:`, response)
        if (config.onJoin) {
          config.onJoin(response)
        }
      })
      .receive("error", (error) => {
        console.error(`Error joining ${channelName}:`, error)
      })

    // Set up event listeners
    if (config.onEvents) {
      Object.entries(config.onEvents).forEach(([event, handler]) => {
        channel.on(event, handler)
      })
    }

    this.channels.set(channelName, channel)
    return channel
  }

  // Send message to specific channel
  sendChannelMessage(channelName, event, payload) {
    const channel = this.channels.get(channelName)
    if (channel) {
      channel.push(event, payload)
        .receive("ok", (response) => {
          console.log(`${channelName}.${event} success:`, response)
        })
        .receive("error", (error) => {
          console.error(`${channelName}.${event} error:`, error)
        })
    } else {
      console.warn(`Channel ${channelName} not found`)
    }
  }

  // Multithreading operations
  initializeMultithreading(config) {
    this.sendChannelMessage("katalyst:main", "multithreading:init", { config })
  }

  submitTask(task) {
    return new Promise((resolve, reject) => {
      const taskId = `task_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
      const taskWithId = { ...task, id: taskId }

      this.sendChannelMessage("katalyst:main", "task:submit", { task: taskWithId })

      // Set up one-time callback for task completion
      const callbackKey = `task_completed_${taskId}`
      this.addCallback(callbackKey, (payload) => {
        if (payload.task_id === taskId) {
          resolve(payload)
          this.removeCallback(callbackKey)
        }
      })

      // Set up timeout
      setTimeout(() => {
        this.removeCallback(callbackKey)
        reject(new Error(`Task ${taskId} timeout`))
      }, 30000) // 30 second timeout
    })
  }

  // Session management
  createSession(type, name) {
    this.sendChannelMessage("katalyst:main", "session:create", { type, name })
  }

  // Terminal operations
  executeCommand(sessionId, command) {
    this.sendChannelMessage("terminal:main", "terminal:execute", { 
      session_id: sessionId, 
      command 
    })
  }

  initializeTerminal(sessionId, config) {
    this.sendChannelMessage("terminal:main", "terminal:init", { 
      session_id: sessionId, 
      config 
    })
  }

  // AI Task operations
  submitAITask(task) {
    return new Promise((resolve, reject) => {
      const taskId = `ai_task_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
      const taskWithId = { ...task, id: taskId }

      this.sendChannelMessage("ai_tasks:main", "ai:submit_task", { task: taskWithId })

      // Set up callback for completion
      const callbackKey = `ai_task_completed_${taskId}`
      this.addCallback(callbackKey, (payload) => {
        if (payload.task_id === taskId) {
          resolve(payload)
          this.removeCallback(callbackKey)
        }
      })

      setTimeout(() => {
        this.removeCallback(callbackKey)
        reject(new Error(`AI Task ${taskId} timeout`))
      }, 60000) // 60 second timeout
    })
  }

  // Callback management
  addCallback(key, callback) {
    if (!this.callbacks.has(key)) {
      this.callbacks.set(key, [])
    }
    this.callbacks.get(key).push(callback)
  }

  removeCallback(key, callback = null) {
    if (callback) {
      const callbacks = this.callbacks.get(key) || []
      const index = callbacks.indexOf(callback)
      if (index > -1) {
        callbacks.splice(index, 1)
      }
    } else {
      this.callbacks.delete(key)
    }
  }

  notifyCallback(key, payload = null) {
    const callbacks = this.callbacks.get(key) || []
    callbacks.forEach(callback => {
      try {
        callback(payload)
      } catch (error) {
        console.error(`Callback error for ${key}:`, error)
      }
    })
  }

  // LiveView integration methods
  updateProps(newProps) {
    this.props = { ...this.props, ...newProps }
    this.notifyCallback('props_updated', this.props)
  }

  handleSessionCreated(session) {
    console.log("Bridge handling session created:", session)
    this.notifyCallback('session_created', session)
  }

  handleSessionClosed(sessionId) {
    console.log("Bridge handling session closed:", sessionId)
    this.notifyCallback('session_closed', { session_id: sessionId })
  }

  handleSessionSelected(sessionId) {
    console.log("Bridge handling session selected:", sessionId)
    this.notifyCallback('session_selected', { session_id: sessionId })
  }

  // Utility methods
  generateSessionId() {
    return `bridge_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
  }

  isConnected() {
    return this.socket && this.socket.isConnected()
  }

  disconnect() {
    if (this.socket) {
      // Leave all channels
      this.channels.forEach((channel, channelName) => {
        console.log(`Leaving channel: ${channelName}`)
        channel.leave()
      })

      // Disconnect socket
      this.socket.disconnect()
      this.socket = null
    }

    // Clear callbacks
    this.callbacks.clear()

    console.log("React-Phoenix bridge disconnected")
  }
}

export default ReactPhoenixBridge

