import React, { useState, useEffect, useCallback, useMemo } from 'react'

// Simplified multithreading store hook for Phoenix integration
function useMultithreadingStore(bridge, config) {
  const [state, setState] = useState({
    workerThreads: config?.worker_threads || 8,
    activeThreads: 0,
    pendingTasks: [],
    runningTasks: [],
    completedTasks: [],
    autoScaling: config?.enable_auto_scaling || true,
    websocketMonitoring: config?.enable_websocket_monitoring || true,
    pubSub: config?.enable_pub_sub || true
  })

  useEffect(() => {
    if (!bridge) return

    // Initialize multithreading with Phoenix
    bridge.initializeMultithreading(config)

    // Set up callbacks for task updates
    bridge.addCallback('task_status_update', (payload) => {
      setState(prev => ({
        ...prev,
        runningTasks: prev.runningTasks.map(task => 
          task.id === payload.task_id 
            ? { ...task, status: payload.status, thread_id: payload.thread_id }
            : task
        )
      }))
    })

    bridge.addCallback('task_completed', (payload) => {
      setState(prev => ({
        ...prev,
        runningTasks: prev.runningTasks.filter(task => task.id !== payload.task_id),
        completedTasks: [...prev.completedTasks, {
          id: payload.task_id,
          result: payload.result,
          duration: payload.duration_ms,
          status: 'completed'
        }]
      }))
    })

  }, [bridge, config])

  const submitTask = useCallback(async (taskParams) => {
    if (!bridge) return null

    const task = {
      id: `task_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      type: taskParams.type || 'cpu',
      operation: taskParams.operation,
      data: taskParams.data,
      priority: taskParams.priority || 'normal',
      status: 'pending'
    }

    setState(prev => ({
      ...prev,
      pendingTasks: [...prev.pendingTasks, task]
    }))

    try {
      const result = await bridge.submitTask(task)
      return result
    } catch (error) {
      console.error('Task submission error:', error)
      return null
    }
  }, [bridge])

  return {
    ...state,
    submitTask
  }
}

// Simplified resource monitoring hook
function useResourceMonitoring(bridge) {
  const [resources, setResources] = useState({
    cpu: 0,
    memory: 0,
    disk: 0,
    network: { in: 0, out: 0 }
  })

  useEffect(() => {
    if (!bridge) return

    bridge.addCallback('resources_updated', (payload) => {
      setResources({
        cpu: payload.cpu || 0,
        memory: payload.memory || 0,
        disk: payload.disk || 0,
        network: payload.network || { in: 0, out: 0 }
      })
    })
  }, [bridge])

  return resources
}

// Resource Monitor Component
function ResourceMonitor({ resources }) {
  const getColorClass = (value) => {
    if (value < 50) return 'bg-green-500'
    if (value < 80) return 'bg-yellow-500'
    return 'bg-red-500'
  }

  return (
    <div className="bg-gray-900/50 rounded-lg p-4 border border-gray-700">
      <h3 className="text-sm font-semibold text-gray-300 mb-3">
        System Resources
      </h3>
      <div className="space-y-3">
        <div>
          <div className="flex justify-between text-xs mb-1">
            <span className="text-gray-400">CPU</span>
            <span className="text-white font-medium">{resources.cpu}%</span>
          </div>
          <div className="h-2 bg-gray-700 rounded-full overflow-hidden">
            <div
              className={`h-full ${getColorClass(resources.cpu)} transition-all duration-300`}
              style={{ width: `${resources.cpu}%` }}
            />
          </div>
        </div>

        <div>
          <div className="flex justify-between text-xs mb-1">
            <span className="text-gray-400">Memory</span>
            <span className="text-white font-medium">{resources.memory}%</span>
          </div>
          <div className="h-2 bg-gray-700 rounded-full overflow-hidden">
            <div
              className={`h-full ${getColorClass(resources.memory)} transition-all duration-300`}
              style={{ width: `${resources.memory}%` }}
            />
          </div>
        </div>

        <div>
          <div className="flex justify-between text-xs mb-1">
            <span className="text-gray-400">Disk</span>
            <span className="text-white font-medium">{resources.disk}%</span>
          </div>
          <div className="h-2 bg-gray-700 rounded-full overflow-hidden">
            <div
              className={`h-full ${getColorClass(resources.disk)} transition-all duration-300`}
              style={{ width: `${resources.disk}%` }}
            />
          </div>
        </div>

        <div className="pt-2 border-t border-gray-700">
          <div className="text-xs text-gray-400">Network</div>
          <div className="flex justify-between mt-1">
            <span className="text-xs">
              <span className="text-gray-500"></span>
              <span className="text-white font-medium ml-1">
                {resources.network.in} MB/s
              </span>
            </span>
            <span className="text-xs">
              <span className="text-gray-500"></span>
              <span className="text-white font-medium ml-1">
                {resources.network.out} MB/s
              </span>
            </span>
          </div>
        </div>
      </div>
    </div>
  )
}

// Session Manager Component
function SessionManager({ sessions, activeSessionId, onSelectSession, onCloseSession, onCreateSession, bridge }) {
  const handleCreateSession = (type) => {
    if (bridge) {
      bridge.createSession(type, `New ${type} Session`)
    }
    if (onCreateSession) {
      onCreateSession(type)
    }
  }

  return (
    <div className="bg-gray-900/50 rounded-lg p-4 border border-gray-700">
      <div className="flex justify-between items-center mb-3">
        <h3 className="text-sm font-semibold text-gray-300">Sessions</h3>
        <div className="flex space-x-1">
          <button
            onClick={() => handleCreateSession('terminal')}
            className="px-2 py-1 bg-cyan-600 hover:bg-cyan-500 rounded text-xs text-white"
            title="New Terminal"
          >

          </button>
          <button
            onClick={() => handleCreateSession('browser')}
            className="px-2 py-1 bg-blue-600 hover:bg-blue-500 rounded text-xs text-white"
            title="New Browser"
          >

          </button>
          <button
            onClick={() => handleCreateSession('ai-agent')}
            className="px-2 py-1 bg-purple-600 hover:bg-purple-500 rounded text-xs text-white"
            title="New AI Agent"
          >

          </button>
        </div>
      </div>

      <div className="space-y-2 max-h-48 overflow-y-auto">
        {sessions.length === 0 ? (
          <div className="text-center text-gray-500 text-xs py-4">
            No active sessions
          </div>
        ) : (
          sessions.map(session => (
            <div
              key={session.id}
              className={`flex items-center justify-between p-2 rounded cursor-pointer ${
                session.id === activeSessionId 
                  ? 'bg-cyan-600/20 border border-cyan-500/30' 
                  : 'bg-gray-800/50 hover:bg-gray-700/50'
              }`}
              onClick={() => onSelectSession && onSelectSession(session.id)}
            >
              <div>
                <div className="text-xs text-white font-medium">{session.name}</div>
                <div className="text-xs text-gray-400">{session.type}</div>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${
                  session.status === 'active' ? 'bg-green-500' : 'bg-gray-500'
                }`} />
                <button
                  onClick={(e) => {
                    e.stopPropagation()
                    onCloseSession && onCloseSession(session.id)
                  }}
                  className="text-gray-400 hover:text-red-400"
                >

                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  )
}

// Terminal Component
function Terminal({ sessionId, bridge }) {
  const [output, setOutput] = useState([])
  const [command, setCommand] = useState('')

  useEffect(() => {
    if (!bridge || !sessionId) return

    // Initialize terminal
    bridge.initializeTerminal(sessionId, {
      rows: 24,
      cols: 80,
      useWebGL: false,
      enableGPU: false
    })

    // Listen for terminal output
    bridge.addCallback('terminal_output', (payload) => {
      setOutput(prev => [...prev, {
        type: payload.type,
        data: payload.data,
        timestamp: payload.timestamp
      }])
    })

    bridge.addCallback('terminal_clear', () => {
      setOutput([])
    })

  }, [bridge, sessionId])

  const executeCommand = (e) => {
    e.preventDefault()
    if (!command.trim() || !bridge || !sessionId) return

    // Add command to output
    setOutput(prev => [...prev, {
      type: 'input',
      data: `$ ${command}`,
      timestamp: new Date().toISOString()
    }])

    // Execute command via bridge
    bridge.executeCommand(sessionId, command)
    setCommand('')
  }

  return (
    <div className="bg-black rounded-lg p-4 font-mono text-sm h-96 flex flex-col">
      <div className="flex-1 overflow-y-auto space-y-1">
        {output.map((line, index) => (
          <div key={index} className={`${
            line.type === 'input' ? 'text-green-400' : 'text-gray-300'
          }`}>
            {line.data}
          </div>
        ))}
      </div>

      <form onSubmit={executeCommand} className="flex mt-2 pt-2 border-t border-gray-700">
        <span className="text-green-400 mr-2">$</span>
        <input
          type="text"
          value={command}
          onChange={(e) => setCommand(e.target.value)}
          className="flex-1 bg-transparent text-white outline-none"
          placeholder="Enter command..."
        />
      </form>
    </div>
  )
}

// Task Queue Component
function TaskQueue({ pendingTasks, runningTasks, completedTasks }) {
  return (
    <div className="bg-gray-900/50 rounded-lg p-4 border border-gray-700">
      <h3 className="text-sm font-semibold text-gray-300 mb-3">
        Task Queue ({pendingTasks.length} pending, {runningTasks.length} running)
      </h3>

      <div className="space-y-2 max-h-48 overflow-y-auto">
        {runningTasks.map(task => (
          <div key={task.id} className="flex items-center justify-between p-2 bg-blue-500/10 rounded">
            <span className="text-xs text-blue-300 truncate">{task.operation}</span>
            <span className="text-xs text-blue-400">Running</span>
          </div>
        ))}

        {pendingTasks.slice(0, 5).map(task => (
          <div key={task.id} className="flex items-center justify-between p-2 bg-gray-500/10 rounded">
            <span className="text-xs text-gray-400 truncate">{task.operation}</span>
            <span className="text-xs text-gray-500">Pending</span>
          </div>
        ))}

        {pendingTasks.length === 0 && runningTasks.length === 0 && (
          <div className="text-center text-gray-500 text-xs py-4">
            No active tasks
          </div>
        )}
      </div>
    </div>
  )
}

// Main Katalyst Advanced Dashboard Component
export function KatalystAdvancedDashboard({
  multithreadingConfig,
  systemMetrics,
  sessions = [],
  activeSessionId,
  bridge,
  onMount,
  onWebSocketConnect
}) {
  // Initialize multithreading store with Phoenix bridge
  const multithreading = useMultithreadingStore(bridge, multithreadingConfig)

  // Resource monitoring
  const resources = useResourceMonitoring(bridge)

  // Connection status
  const [isConnected, setIsConnected] = useState(false)

  useEffect(() => {
    // Notify parent component that React has mounted
    if (onMount) {
      onMount()
    }

    // Set up bridge connection callbacks
    if (bridge) {
      bridge.addCallback('socket_connected', () => {
        setIsConnected(true)
        if (onWebSocketConnect) {
          onWebSocketConnect()
        }
      })

      bridge.addCallback('socket_disconnected', () => {
        setIsConnected(false)
      })
    }
  }, [bridge, onMount, onWebSocketConnect])

  // Find active session
  const activeSession = sessions.find(s => s.id === activeSessionId)

  // Compute real-time metrics
  const computedMetrics = useMemo(() => ({
    totalSessions: sessions.length,
    activeSessions: sessions.filter(s => s.status === 'active').length,
    threadUtilization: systemMetrics?.thread_utilization || 0,
    memoryUsage: systemMetrics?.memory_usage || 0,
    taskThroughput: systemMetrics?.task_throughput || 0,
    queueBacklog: multithreading.pendingTasks.length,
    activeThreads: multithreading.runningTasks.length,
    completedTasks: multithreading.completedTasks.length,
  }), [sessions, systemMetrics, multithreading])

  return (
    <div className="w-full h-full text-white">
      {/* Connection Status */}
      <div className="mb-4 flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <div className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-500 animate-pulse' : 'bg-red-500'}`} />
          <span className="text-sm text-gray-300">
            {isConnected ? 'Connected to Phoenix' : 'Disconnected'}
          </span>
        </div>
        <div className="text-xs text-gray-400">
          React Dashboard Active  {multithreading.workerThreads} workers
        </div>
      </div>

      {/* Main Dashboard Grid */}
      <div className="grid grid-cols-12 gap-6 h-full">
        {/* Main Content Area */}
        <div className="col-span-8 space-y-6">
          {/* Terminal/Active Session */}
          {activeSession && (
            <div className="bg-gray-900/50 rounded-lg p-4 border border-gray-700">
              <h3 className="text-sm font-semibold text-gray-300 mb-3">
                Active Session: {activeSession.name}
              </h3>
              {activeSession.type === 'terminal' && (
                <Terminal sessionId={activeSession.id} bridge={bridge} />
              )}
              {activeSession.type === 'browser' && (
                <div className="bg-gray-800 rounded p-4 h-96 flex items-center justify-center">
                  <span className="text-gray-400">Browser session not implemented yet</span>
                </div>
              )}
              {activeSession.type === 'ai-agent' && (
                <div className="bg-gray-800 rounded p-4 h-96 flex items-center justify-center">
                  <span className="text-gray-400">AI Agent session not implemented yet</span>
                </div>
              )}
            </div>
          )}

          {/* Task Queue */}
          <TaskQueue
            pendingTasks={multithreading.pendingTasks}
            runningTasks={multithreading.runningTasks}
            completedTasks={multithreading.completedTasks}
          />
        </div>

        {/* Sidebar */}
        <div className="col-span-4 space-y-6">
          {/* Session Manager */}
          <SessionManager
            sessions={sessions}
            activeSessionId={activeSessionId}
            onSelectSession={(sessionId) => {
              // Notify LiveView of session selection
              bridge?.liveViewHook?.pushEvent('select_session', { session_id: sessionId })
            }}
            onCloseSession={(sessionId) => {
              // Notify LiveView of session closure
              bridge?.liveViewHook?.pushEvent('close_session', { session_id: sessionId })
            }}
            onCreateSession={(type) => {
              // Notify LiveView to create session
              bridge?.liveViewHook?.pushEvent('create_session', { type })
            }}
            bridge={bridge}
          />

          {/* Resource Monitor */}
          <ResourceMonitor resources={resources} />

          {/* System Metrics */}
          <div className="bg-gray-900/50 rounded-lg p-4 border border-gray-700">
            <h3 className="text-sm font-semibold text-gray-300 mb-3">System Metrics</h3>
            <div className="space-y-2 text-xs">
              <div className="flex justify-between">
                <span className="text-gray-400">Sessions:</span>
                <span className="text-white">{computedMetrics.activeSessions}/{computedMetrics.totalSessions}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Active Tasks:</span>
                <span className="text-white">{computedMetrics.activeThreads}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Queue Backlog:</span>
                <span className="text-white">{computedMetrics.queueBacklog}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Completed:</span>
                <span className="text-white">{computedMetrics.completedTasks}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default KatalystAdvancedDashboard

