    pub innovation: f64,
    pub reliability: f64,
    pub speed: f64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Reward {
    pub amount: BigDecimal,
    pub metrics: PerformanceMetrics,
    pub timestamp: DateTime<Utc>,
    pub transaction_id: Uuid,
}

impl Reward {
    pub fn new(amount: BigDecimal, metrics: PerformanceMetrics) -> Self {
        Reward {
            amount,
            metrics,
            timestamp: Utc::now(),
            transaction_id: Uuid::new_v4(),
        }
    }
}

#[derive(Debug, Clone)]
pub struct TokenPool {
    pub total_supply: BigDecimal,
    pub available_balance: BigDecimal,
    pub locked_balance: BigDecimal,
    pub distribution_history: Vec<(AgentId, BigDecimal, DateTime<Utc>)>,
}

impl TokenPool {
    pub fn new(initial_supply: BigDecimal) -> Self {
        TokenPool {
            total_supply: initial_supply.clone(),
            available_balance: initial_supply,
            locked_balance: BigDecimal::from(0),
            distribution_history: Vec::new(),
        }
    }

    pub fn transfer(&mut self, agent_id: AgentId, amount: BigDecimal) -> Result<()> {
        if self.available_balance < amount {
            return Err(IncentiveError::InsufficientBalance.into());
        }

        self.available_balance -= &amount;
        self.distribution_history.push((agent_id, amount, Utc::now()));

        Ok(())
    }
}

pub struct PerformanceTracker {
    agent_history: Arc<DashMap<AgentId, Vec<PerformanceMetrics>>>,
    global_metrics: Arc<RwLock<PerformanceMetrics>>,
}

impl PerformanceTracker {
    pub fn new() -> Self {
        PerformanceTracker {
            agent_history: Arc::new(DashMap::new()),
            global_metrics: Arc::new(RwLock::new(PerformanceMetrics {
                accuracy: 0.0,
                efficiency: 0.0,
                innovation: 0.0,
                reliability: 0.0,
                speed: 0.0,
            })),
        }
    }

    pub fn evaluate(&self, task_result: &TaskResult) -> PerformanceMetrics {
        // Calculate performance based on task result
        let accuracy = if task_result.success { 
            task_result.quality_score 
        } else { 
            0.0 
        };

        let efficiency = 1.0 / (1.0 + task_result.resource_usage.cpu_seconds / 100.0);

        let speed = 1.0 / (1.0 + task_result.execution_time_ms as f64 / 1000.0);

        let metrics = PerformanceMetrics {
            accuracy,
            efficiency,
            innovation: task_result.innovation_score,
            reliability: if task_result.success { 1.0 } else { 0.0 },
            speed,
        };

        // Store in history
        self.agent_history
            .entry(task_result.agent_id.clone())
            .or_insert_with(Vec::new)
            .push(metrics.clone());

        // Update global metrics
        let mut global = self.global_metrics.write();
        global.accuracy = (global.accuracy + accuracy) / 2.0;
        global.efficiency = (global.efficiency + efficiency) / 2.0;
        global.innovation = (global.innovation + task_result.innovation_score) / 2.0;
        global.reliability = (global.reliability + metrics.reliability) / 2.0;
        global.speed = (global.speed + speed) / 2.0;

        metrics
    }

    pub fn get_agent_history(&self, agent_id: &AgentId) -> Vec<PerformanceMetrics> {
        self.agent_history
            .get(agent_id)
            .map(|entry| entry.clone())
            .unwrap_or_default()
    }
}

pub struct RewardCalculator {
    base_reward: BigDecimal,
    difficulty_multiplier: f64,
    innovation_bonus: f64,
    quality_bonus: f64,
}

impl RewardCalculator {
    pub fn new() -> Self {
        RewardCalculator {
            base_reward: BigDecimal::from_str("100").unwrap(),
            difficulty_multiplier: 2.0,
            innovation_bonus: 1.5,
            quality_bonus: 1.2,
        }
    }

    pub fn calculate(
        &self,
        metrics: PerformanceMetrics,
        difficulty: f64,
        innovation_score: f64,
    ) -> BigDecimal {
        let mut reward = self.base_reward.clone();

        // Apply difficulty multiplier
        let difficulty_factor = BigDecimal::from_str(&format!("{}", 
            1.0 + (difficulty * self.difficulty_multiplier))).unwrap();
        reward *= difficulty_factor;

        // Apply performance multipliers
        let performance_factor = BigDecimal::from_str(&format!("{}", 
            metrics.accuracy * metrics.efficiency * metrics.reliability)).unwrap();
        reward *= performance_factor;

        // Apply innovation bonus
        if innovation_score > 0.7 {
            let innovation_factor = BigDecimal::from_str(&format!("{}", 
                1.0 + (innovation_score * self.innovation_bonus))).unwrap();
            reward *= innovation_factor;
        }

        // Apply quality bonus
        if metrics.accuracy > 0.9 {
            let quality_factor = BigDecimal::from_str(&format!("{}", 
                self.quality_bonus)).unwrap();
            reward *= quality_factor;
        }

        reward
    }
}

pub struct ResourceAllocator {
    resource_pools: Arc<DashMap<String, ResourcePool>>,
    allocation_strategy: AllocationStrategy,
}

#[derive(Debug, Clone)]
pub struct ResourcePool {
    pub resource_type: String,
    pub total_capacity: f64,
    pub available_capacity: f64,
    pub allocations: HashMap<AgentId, f64>,
}

#[derive(Debug, Clone)]
pub enum AllocationStrategy {
    Proportional,
    PriorityBased,
    MarketDriven,
}

impl ResourceAllocator {
    pub fn new(strategy: AllocationStrategy) -> Self {
        ResourceAllocator {
            resource_pools: Arc::new(DashMap::new()),
            allocation_strategy: strategy,
        }
    }

    pub async fn allocate_resources(
        &self,
        agent_id: AgentId,
        reward_amount: &BigDecimal,
    ) -> Result<()> {
        // Convert reward to resource allocation
        let cpu_allocation = reward_amount.clone() / BigDecimal::from(100);
        let memory_allocation = reward_amount.clone() / BigDecimal::from(50);
        let gpu_allocation = reward_amount.clone() / BigDecimal::from(1000);

        // Update CPU allocation
        self.update_allocation("cpu", agent_id.clone(), cpu_allocation.to_string().parse()?).await?;

        // Update Memory allocation
        self.update_allocation("memory", agent_id.clone(), memory_allocation.to_string().parse()?).await?;

        // Update GPU allocation
        self.update_allocation("gpu", agent_id.clone(), gpu_allocation.to_string().parse()?).await?;

        Ok(())
    }

    async fn update_allocation(
        &self,
        resource_type: &str,
        agent_id: AgentId,
        amount: f64,
    ) -> Result<()> {
        let mut pool = self.resource_pools.entry(resource_type.to_string())
            .or_insert_with(|| ResourcePool {
                resource_type: resource_type.to_string(),
                total_capacity: 1000.0,
                available_capacity: 1000.0,
                allocations: HashMap::new(),
            });

        if pool.available_capacity < amount {
            return Err(IncentiveError::ResourceAllocationFailed(
                format!("Insufficient {} capacity", resource_type)
            ).into());
        }

        pool.available_capacity -= amount;
        *pool.allocations.entry(agent_id).or_insert(0.0) += amount;

        Ok(())
    }
}

pub struct IncentiveEngine {
    token_pool: Arc<TokioRwLock<TokenPool>>,
    performance_tracker: Arc<PerformanceTracker>,
    reward_calculator: Arc<RewardCalculator>,
    resource_allocator: Arc<ResourceAllocator>,
    event_sender: broadcast::Sender<IncentiveEvent>,
    metrics_registry: Arc<Registry>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum IncentiveEvent {
    RewardIssued {
        agent_id: AgentId,
        amount: String,
        timestamp: DateTime<Utc>,
    },
    ResourceAllocated {
        agent_id: AgentId,
        resource_type: String,
        amount: f64,
    },
    PerformanceUpdate {
        agent_id: AgentId,
        metrics: PerformanceMetrics,
    },
}

impl IncentiveEngine {
    pub fn new(initial_token_supply: BigDecimal) -> Self {
        let (event_sender, _) = broadcast::channel(1000);
        let metrics_registry = Arc::new(Registry::new());

        IncentiveEngine {
            token_pool: Arc::new(TokioRwLock::new(TokenPool::new(initial_token_supply))),
            performance_tracker: Arc::new(PerformanceTracker::new()),
            reward_calculator: Arc::new(RewardCalculator::new()),
            resource_allocator: Arc::new(ResourceAllocator::new(AllocationStrategy::Proportional)),
            event_sender,
            metrics_registry,
        }
    }

    pub async fn reward_ai_performance(
        &self,
        agent_id: AgentId,
        task_result: TaskResult,
    ) -> Result<Reward> {
        info!("Processing reward for agent {:?}", agent_id);

        // Evaluate performance
        let performance_metrics = self.performance_tracker.evaluate(&task_result);

        // Calculate reward amount
        let reward_amount = self.reward_calculator.calculate(
            performance_metrics.clone(),
            task_result.difficulty,
            task_result.innovation_score,
        );

        // Distribute tokens
        {
            let mut pool = self.token_pool.write().await;
            pool.transfer(agent_id.clone(), reward_amount.clone())?;
        }

        // Allocate resources based on performance
        self.resource_allocator
            .allocate_resources(agent_id.clone(), &reward_amount)
            .await?;

        // Create reward record
        let reward = Reward::new(reward_amount.clone(), performance_metrics.clone());

        // Emit events
        let _ = self.event_sender.send(IncentiveEvent::RewardIssued {
            agent_id: agent_id.clone(),
            amount: reward_amount.to_string(),
            timestamp: Utc::now(),
        });

        let _ = self.event_sender.send(IncentiveEvent::PerformanceUpdate {
            agent_id,
            metrics: performance_metrics,
        });

        info!("Reward issued successfully: {:?}", reward);

        Ok(reward)
    }

    pub async fn get_agent_balance(&self, agent_id: &AgentId) -> Result<BigDecimal> {
        let pool = self.token_pool.read().await;

        let balance = pool.distribution_history
            .iter()
            .filter(|(id, _, _)| id.0 == agent_id.0)
            .map(|(_, amount, _)| amount.clone())
            .fold(BigDecimal::from(0), |acc, x| acc + x);

        Ok(balance)
    }

    pub fn get_agent_performance(&self, agent_id: &AgentId) -> Vec<PerformanceMetrics> {
        self.performance_tracker.get_agent_history(agent_id)
    }

    pub fn subscribe_events(&self) -> broadcast::Receiver<IncentiveEvent> {
        self.event_sender.subscribe()
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_reward_calculation() {
        let engine = IncentiveEngine::new(BigDecimal::from_str("1000000").unwrap());
        let agent_id = AgentId::new();

        let task_result = TaskResult {
            task_id: Uuid::new_v4(),
            agent_id: agent_id.clone(),
            success: true,
            difficulty: 0.8,
            execution_time_ms: 500,
            innovation_score: 0.9,
            quality_score: 0.95,
            resource_usage: ResourceUsage {
                cpu_seconds: 10.0,
                memory_mb: 512.0,
                gpu_hours: 0.1,
                network_gb: 1.0,
            },
            timestamp: Utc::now(),
        };

        let reward = engine.reward_ai_performance(agent_id.clone(), task_result).await.unwrap();

        assert!(reward.amount > BigDecimal::from(0));
        assert!(reward.metrics.accuracy > 0.0);

        let balance = engine.get_agent_balance(&agent_id).await.unwrap();
        assert_eq!(balance, reward.amount);
    }

    #[tokio::test]
    async fn test_insufficient_balance() {
        let engine = IncentiveEngine::new(BigDecimal::from_str("100").unwrap());
        let agent_id = AgentId::new();

        // Create a task that would generate high reward
        let task_result = TaskResult {
            task_id: Uuid::new_v4(),
            agent_id: agent_id.clone(),
            success: true,
            difficulty: 1.0,
            execution_time_ms: 100,
            innovation_score: 1.0,
            quality_score: 1.0,
            resource_usage: ResourceUsage {
                cpu_seconds: 1.0,
                memory_mb: 100.0,
                gpu_hours: 0.01,
                network_gb: 0.1,
            },
            timestamp: Utc::now(),
        };

        // First reward should succeed
        let result1 = engine.reward_ai_performance(agent_id.clone(), task_result.clone()).await;
        assert!(result1.is_ok());

        // Eventually we should run out of tokens
        let mut exhausted = false;
        for _ in 0..100 {
            let result = engine.reward_ai_performance(agent_id.clone(), task_result.clone()).await;
            if result.is_err() {
                exhausted = true;
                break;
            }
        }
        assert!(exhausted);
    }
}

