# Variables
DOCKER_COMPOSE := docker-compose -f docker-compose.specializations.yml
DOCKER_COMPOSE_DEV := $(DOCKER_COMPOSE) --profile dev
PROJECT_NAME := manifolded-specializations
REGISTRY := localhost:5000
VERSION := $(shell git describe --tags --always --dirty)
TIMESTAMP := $(shell date +%Y%m%d%H%M%S)

# Color output
RED := \033[0;31m
GREEN := \033[0;32m
YELLOW := \033[1;33m
BLUE := \033[0;34m
NC := \033[0m # No Color

# ============================================
# Help and Default Target
# ============================================
help: ## Show this help message
	@echo "$(BLUE)Manifolded Specializations Platform$(NC)"
	@echo "$(YELLOW)=================================$(NC)"
	@echo ""
	@echo "Available targets:"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | sort | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(GREEN)%-20s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(YELLOW)Quick Start:$(NC)"
	@echo "  make setup       # Initial setup"
	@echo "  make build       # Build all services"
	@echo "  make up          # Start all services"
	@echo "  make test        # Run all tests"
	@echo ""

# ============================================
# Setup and Installation
# ============================================
setup: ## Initial setup and dependency installation
	@echo "$(BLUE)Setting up Manifolded Specializations Platform...$(NC)"
	@./scripts/setup.sh
	@echo "$(GREEN)Creating necessary directories...$(NC)"
	@mkdir -p monitoring/prometheus/rules
	@mkdir -p monitoring/grafana/{provisioning,dashboards}
	@mkdir -p monitoring/loki
	@mkdir -p monitoring/promtail
	@mkdir -p nginx/conf.d
	@mkdir -p certs
	@mkdir -p sql
	@echo "$(GREEN)Setup complete!$(NC)"

install-deps: ## Install all dependencies
	@echo "$(BLUE)Installing dependencies...$(NC)"
	# Julia dependencies
	@cd specializations/integration/quantum-economic-bridge && julia --project=. -e 'using Pkg; Pkg.instantiate()'
	# Rust dependencies
	@cd specializations/integration/ai-economic-incentives && cargo build --release
	# Node dependencies
	@cd specializations/integration/quantum-ai-consciousness && npm ci
	# Elixir dependencies
	@cd crates/manifolded-phoenix-legacy && mix deps.get
	@echo "$(GREEN)Dependencies installed!$(NC)"

# ============================================
# Docker Build Commands
# ============================================
build: ## Build all Docker images
	@echo "$(BLUE)Building all services...$(NC)"
	$(DOCKER_COMPOSE) build --parallel
	@echo "$(GREEN)Build complete!$(NC)"

build-quantum: ## Build quantum engine
	@echo "$(BLUE)Building quantum engine...$(NC)"
	$(DOCKER_COMPOSE) build quantum-engine
	@echo "$(GREEN)Quantum engine built!$(NC)"

build-economic: ## Build economic engine
	@echo "$(BLUE)Building economic engine...$(NC)"
	$(DOCKER_COMPOSE) build economic-engine
	@echo "$(GREEN)Economic engine built!$(NC)"

build-ai: ## Build AI consciousness
	@echo "$(BLUE)Building AI consciousness...$(NC)"
	$(DOCKER_COMPOSE) build ai-consciousness
	@echo "$(GREEN)AI consciousness built!$(NC)"

build-integration: ## Build integration services
	@echo "$(BLUE)Building integration services...$(NC)"
	$(DOCKER_COMPOSE) build quantum-economic-bridge ai-economic-incentives quantum-ai-consciousness
	@echo "$(GREEN)Integration services built!$(NC)"

build-phoenix: ## Build Phoenix dashboard
	@echo "$(BLUE)Building Phoenix dashboard...$(NC)"
	$(DOCKER_COMPOSE) build phoenix-dashboard
	@echo "$(GREEN)Phoenix dashboard built!$(NC)"

# ============================================
# Docker Run Commands
# ============================================
up: ## Start all services
	@echo "$(BLUE)Starting all services...$(NC)"
	$(DOCKER_COMPOSE) up -d
	@echo "$(GREEN)All services started!$(NC)"
	@echo "$(YELLOW)Waiting for services to be healthy...$(NC)"
	@sleep 10
	@make status

up-dev: ## Start all services with dev tools
	@echo "$(BLUE)Starting services with development tools...$(NC)"
	$(DOCKER_COMPOSE_DEV) up -d
	@echo "$(GREEN)Services started with dev tools!$(NC)"

down: ## Stop all services
	@echo "$(YELLOW)Stopping all services...$(NC)"
	$(DOCKER_COMPOSE) down
	@echo "$(GREEN)All services stopped!$(NC)"

restart: ## Restart all services
	@echo "$(YELLOW)Restarting all services...$(NC)"
	$(DOCKER_COMPOSE) restart
	@echo "$(GREEN)All services restarted!$(NC)"

# ============================================
# Service Management
# ============================================
status: ## Show status of all services
	@echo "$(BLUE)Service Status:$(NC)"
	@echo "$(YELLOW)=================================$(NC)"
	@$(DOCKER_COMPOSE) ps
	@echo ""
	@echo "$(BLUE)Health Checks:$(NC)"
	@echo "$(YELLOW)=================================$(NC)"
	@for service in quantum-engine economic-engine ai-consciousness phoenix-dashboard; do \
		health=$$(docker inspect --format='{{.State.Health.Status}}' manifolded-$$service 2>/dev/null || echo "N/A"); \
		if [ "$$health" = "healthy" ]; then \
			echo "$(GREEN)$(NC) $$service: $$health"; \
		else \
			echo "$(RED)$(NC) $$service: $$health"; \
		fi; \
	done

logs: ## Show logs from all services
	$(DOCKER_COMPOSE) logs -f

logs-quantum: ## Show quantum engine logs
	$(DOCKER_COMPOSE) logs -f quantum-engine

logs-economic: ## Show economic engine logs
	$(DOCKER_COMPOSE) logs -f economic-engine

logs-ai: ## Show AI consciousness logs
	$(DOCKER_COMPOSE) logs -f ai-consciousness

logs-phoenix: ## Show Phoenix dashboard logs
	$(DOCKER_COMPOSE) logs -f phoenix-dashboard

# ============================================
# Testing
# ============================================
test: ## Run all tests
	@echo "$(BLUE)Running all tests...$(NC)"
	@make test-unit
	@make test-integration
	@make test-e2e
	@echo "$(GREEN)All tests passed!$(NC)"

test-unit: ## Run unit tests
	@echo "$(BLUE)Running unit tests...$(NC)"
	# Julia tests
	@cd specializations/integration/quantum-economic-bridge && julia --project=. test/runtests.jl
	# Rust tests
	@cd specializations/integration/ai-economic-incentives && cargo test
	# TypeScript tests
	@cd specializations/integration/quantum-ai-consciousness && npm test
	@echo "$(GREEN)Unit tests complete!$(NC)"

test-integration: ## Run integration tests
	@echo "$(BLUE)Running integration tests...$(NC)"
	@docker-compose -f docker-compose.test.yml up --abort-on-container-exit
	@echo "$(GREEN)Integration tests complete!$(NC)"

test-e2e: ## Run end-to-end tests
	@echo "$(BLUE)Running end-to-end tests...$(NC)"
	@./scripts/test-e2e.sh
	@echo "$(GREEN)E2E tests complete!$(NC)"

benchmark: ## Run performance benchmarks
	@echo "$(BLUE)Running benchmarks...$(NC)"
	# Quantum-Economic Bridge benchmark
	@docker exec manifolded-qe-bridge julia -e 'include("/app/quantum_economic_bridge.jl"); using .QuantumEconomicBridge; QuantumEconomicBridge.benchmark_transfer(1000)'
	# AI-Economic Incentives benchmark
	@docker exec manifolded-ai-incentives cargo bench
	# Quantum-AI Consciousness benchmark
	@docker exec manifolded-quantum-ai npm run benchmark
	@echo "$(GREEN)Benchmarks complete!$(NC)"

# ============================================
# Monitoring
# ============================================
monitor: ## Open monitoring dashboards
	@echo "$(BLUE)Opening monitoring dashboards...$(NC)"
	@echo "$(GREEN)Grafana:$(NC) http://localhost:3000 (admin/admin)"
	@echo "$(GREEN)Prometheus:$(NC) http://localhost:9090"
	@echo "$(GREEN)Jaeger:$(NC) http://localhost:16686"
	@echo "$(GREEN)Phoenix:$(NC) http://localhost:4000"

metrics: ## Show current metrics
	@echo "$(BLUE)Current Metrics:$(NC)"
	@curl -s http://localhost:9091/metrics | grep -E "^quantum_|^economic_|^ai_" | head -20

alerts: ## Show active alerts
	@echo "$(BLUE)Active Alerts:$(NC)"
	@curl -s http://localhost:9090/api/v1/alerts | jq '.data.alerts'

# ============================================
# Database Management
# ============================================
db-migrate: ## Run database migrations
	@echo "$(BLUE)Running database migrations...$(NC)"
	@docker exec manifolded-postgres-spec psql -U postgres -d manifolded_economic -f /docker-entrypoint-initdb.d/init.sql
	@docker exec manifolded-phoenix mix ecto.migrate
	@echo "$(GREEN)Migrations complete!$(NC)"

db-seed: ## Seed database with test data
	@echo "$(BLUE)Seeding database...$(NC)"
	@docker exec manifolded-phoenix mix run priv/repo/seeds.exs
	@echo "$(GREEN)Database seeded!$(NC)"

db-reset: ## Reset database
	@echo "$(RED)Resetting database...$(NC)"
	@docker exec manifolded-phoenix mix ecto.reset
	@echo "$(GREEN)Database reset complete!$(NC)"

# ============================================
# Deployment
# ============================================
deploy-local: ## Deploy to local environment
	@echo "$(BLUE)Deploying to local environment...$(NC)"
	@make build
	@make up
	@make db-migrate
	@echo "$(GREEN)Local deployment complete!$(NC)"

deploy-staging: ## Deploy to staging environment
	@echo "$(BLUE)Deploying to staging...$(NC)"
	@./scripts/deploy-staging.sh
	@echo "$(GREEN)Staging deployment complete!$(NC)"

deploy-production: ## Deploy to production environment
	@echo "$(RED)Deploying to PRODUCTION...$(NC)"
	@echo "$(YELLOW)Are you sure? [y/N]$(NC)"
	@read -r REPLY; \
	if [ "$$REPLY" = "y" ] || [ "$$REPLY" = "Y" ]; then \
		./scripts/deploy-production.sh; \
		echo "$(GREEN)Production deployment complete!$(NC)"; \
	else \
		echo "$(YELLOW)Deployment cancelled$(NC)"; \
	fi

# ============================================
# Cleanup
# ============================================
clean: ## Clean up containers and volumes
	@echo "$(RED)Cleaning up...$(NC)"
	$(DOCKER_COMPOSE) down -v
	@echo "$(GREEN)Cleanup complete!$(NC)"

clean-all: ## Clean everything including images
	@echo "$(RED)Cleaning everything...$(NC)"
	$(DOCKER_COMPOSE) down -v --rmi all
	@docker system prune -af
	@echo "$(GREEN)Full cleanup complete!$(NC)"

prune: ## Prune Docker system
	@echo "$(YELLOW)Pruning Docker system...$(NC)"
	@docker system prune -af --volumes
	@echo "$(GREEN)Prune complete!$(NC)"

# ============================================
# Development Helpers
# ============================================
shell-quantum: ## Open shell in quantum engine
	@docker exec -it manifolded-quantum /bin/sh

shell-economic: ## Open shell in economic engine
	@docker exec -it manifolded-economic /bin/sh

shell-ai: ## Open shell in AI consciousness
	@docker exec -it manifolded-ai /bin/bash

shell-phoenix: ## Open shell in Phoenix dashboard
	@docker exec -it manifolded-phoenix /bin/bash

iex: ## Open IEx console in Phoenix
	@docker exec -it manifolded-phoenix iex -S mix

julia-repl: ## Open Julia REPL in quantum-economic bridge
	@docker exec -it manifolded-qe-bridge julia

psql: ## Connect to PostgreSQL
	@docker exec -it manifolded-postgres-spec psql -U postgres

redis-cli: ## Connect to Redis
	@docker exec -it manifolded-redis-spec redis-cli

# ============================================
# Documentation
# ============================================
docs: ## Generate documentation
	@echo "$(BLUE)Generating documentation...$(NC)"
	@cd specializations/integration/quantum-economic-bridge && julia --project=. -e 'using Documenter; makedocs()'
	@cd specializations/integration/ai-economic-incentives && cargo doc --no-deps
	@cd specializations/integration/quantum-ai-consciousness && npm run docs
	@echo "$(GREEN)Documentation generated!$(NC)"

# ============================================
# CI/CD
# ============================================
ci: ## Run CI pipeline
	@echo "$(BLUE)Running CI pipeline...$(NC)"
	@make lint
	@make test
	@make build
	@echo "$(GREEN)CI pipeline complete!$(NC)"

lint: ## Run linters
	@echo "$(BLUE)Running linters...$(NC)"
	# Rust
	@cd specializations/integration/ai-economic-incentives && cargo clippy -- -D warnings
	# TypeScript
	@cd specializations/integration/quantum-ai-consciousness && npm run lint
	# Elixir
	@cd crates/manifolded-phoenix-legacy && mix credo
	@echo "$(GREEN)Linting complete!$(NC)"

format: ## Format code
	@echo "$(BLUE)Formatting code...$(NC)"
	# Rust
	@cd specializations/integration/ai-economic-incentives && cargo fmt
	# TypeScript
	@cd specializations/integration/quantum-ai-consciousness && npm run format
	# Elixir
	@cd crates/manifolded-phoenix-legacy && mix format
	@echo "$(GREEN)Formatting complete!$(NC)"

# ============================================
# Version Management
# ============================================
version: ## Show current version
	@echo "$(BLUE)Current version: $(GREEN)$(VERSION)$(NC)"

tag: ## Create a new version tag
	@echo "$(BLUE)Creating new tag...$(NC)"
	@read -p "Enter version (e.g., v1.0.0): " version; \
	git tag -a $$version -m "Release $$version"; \
	git push origin $$version
	@echo "$(GREEN)Tag created!$(NC)"

# Default target
.DEFAULT_GOAL := help

