# NGINX API Gateway Configuration for Manifolded Platform
# Advanced load balancing, rate limiting, and security features

user nginx;
worker_processes auto;
worker_rlimit_nofile 65535;
error_log /var/log/nginx/error.log warn;
pid /var/run/nginx.pid;

events {
    worker_connections 4096;
    use epoll;
    multi_accept on;
}

http {
    include /etc/nginx/mime.types;
    default_type application/octet-stream;

    # Logging format with tracing support
    log_format main_ext '$remote_addr - $remote_user [$time_local] "$request" '
                        '$status $body_bytes_sent "$http_referer" '
                        '"$http_user_agent" "$http_x_forwarded_for" '
                        'rt=$request_time uct="$upstream_connect_time" '
                        'uht="$upstream_header_time" urt="$upstream_response_time" '
                        'trace_id=$http_x_trace_id';

    access_log /var/log/nginx/access.log main_ext buffer=32k;

    # Performance optimizations
    sendfile on;
    tcp_nopush on;
    tcp_nodelay on;
    keepalive_timeout 65;
    keepalive_requests 100;
    reset_timedout_connection on;
    client_body_timeout 10;
    client_header_timeout 10;
    send_timeout 10;

    # Security headers
    add_header X-Frame-Options "SAMEORIGIN" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header X-XSS-Protection "1; mode=block" always;
    add_header Referrer-Policy "strict-origin-when-cross-origin" always;
    add_header Content-Security-Policy "default-src 'self' http: https: data: blob: 'unsafe-inline'" always;

    # Rate limiting zones
    limit_req_zone $binary_remote_addr zone=general:10m rate=10r/s;
    limit_req_zone $binary_remote_addr zone=api:10m rate=100r/s;
    limit_req_zone $binary_remote_addr zone=quantum:10m rate=50r/s;
    limit_req_zone $binary_remote_addr zone=economic:10m rate=200r/s;
    limit_req_zone $binary_remote_addr zone=ai:10m rate=30r/s;

    # Connection limiting
    limit_conn_zone $binary_remote_addr zone=addr:10m;
    limit_conn addr 100;

    # Gzip compression
    gzip on;
    gzip_vary on;
    gzip_proxied any;
    gzip_comp_level 6;
    gzip_types text/plain text/css text/xml text/javascript 
               application/json application/javascript application/xml+rss 
               application/rss+xml application/atom+xml image/svg+xml 
               text/x-js text/x-cross-domain-policy application/x-font-ttf 
               application/x-font-opentype application/vnd.ms-fontobject 
               image/x-icon;

    # Cache settings
    proxy_cache_path /var/cache/nginx/api levels=1:2 keys_zone=api_cache:100m 
                     max_size=1g inactive=60m use_temp_path=off;
    proxy_cache_path /var/cache/nginx/static levels=1:2 keys_zone=static_cache:100m 
                     max_size=10g inactive=7d use_temp_path=off;

    # Upstream definitions with health checks
    upstream quantum_backend {
        least_conn;
        server quantum-engine:8080 max_fails=3 fail_timeout=30s;
        keepalive 32;
    }

    upstream economic_backend {
        ip_hash;
        server economic-engine:8080 max_fails=3 fail_timeout=30s;
        server economic-engine-2:8080 max_fails=3 fail_timeout=30s backup;
        server economic-engine-3:8080 max_fails=3 fail_timeout=30s backup;
        keepalive 64;
    }

    upstream ai_backend {
        least_conn;
        server ai-consciousness:8080 max_fails=3 fail_timeout=30s;
        keepalive 32;
    }

    upstream phoenix_backend {
        least_conn;
        server phoenix-dashboard:4000 max_fails=3 fail_timeout=30s;
        keepalive 32;
    }

    upstream integration_backend {
        least_conn;
        server quantum-economic-bridge:8080 max_fails=3 fail_timeout=30s;
        server ai-economic-incentives:8080 max_fails=3 fail_timeout=30s;
        server quantum-ai-consciousness:8080 max_fails=3 fail_timeout=30s;
        keepalive 32;
    }

    # WebSocket support
    map $http_upgrade $connection_upgrade {
        default upgrade;
        '' close;
    }

    # JWT validation map
    map $http_authorization $jwt_token {
        ~^Bearer\s+(.*) $1;
    }

    # Include additional configurations
    include /etc/nginx/conf.d/*.conf;

    # Main API Gateway Server
    server {
        listen 80 default_server;
        listen [::]:80 default_server;
        server_name _;

        # Redirect HTTP to HTTPS
        return 301 https://$host$request_uri;
    }

    server {
        listen 443 ssl http2 default_server;
        listen [::]:443 ssl http2 default_server;
        server_name api.manifolded.ai;

        # SSL Configuration
        ssl_certificate /etc/nginx/certs/fullchain.pem;
        ssl_certificate_key /etc/nginx/certs/privkey.pem;
        ssl_session_timeout 1d;
        ssl_session_cache shared:SSL:50m;
        ssl_session_tickets off;

        # Modern SSL configuration
        ssl_protocols TLSv1.2 TLSv1.3;
        ssl_ciphers ECDHE-ECDSA-AES128-GCM-SHA256:ECDHE-RSA-AES128-GCM-SHA256:ECDHE-ECDSA-AES256-GCM-SHA384:ECDHE-RSA-AES256-GCM-SHA384;
        ssl_prefer_server_ciphers off;

        # OCSP stapling
        ssl_stapling on;
        ssl_stapling_verify on;
        ssl_trusted_certificate /etc/nginx/certs/chain.pem;

        # Security headers
        add_header Strict-Transport-Security "max-age=63072000" always;

        # Request ID generation for tracing
        set $request_id $request_id;
        add_header X-Request-ID $request_id always;

        # Default location
        location / {
            limit_req zone=general burst=20 nodelay;

            proxy_pass http://phoenix_backend;
            proxy_http_version 1.1;
            proxy_set_header Upgrade $http_upgrade;
            proxy_set_header Connection $connection_upgrade;
            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
            proxy_set_header X-Forwarded-Proto $scheme;
            proxy_set_header X-Request-ID $request_id;

            # Timeouts
            proxy_connect_timeout 60s;
            proxy_send_timeout 60s;
            proxy_read_timeout 60s;
        }

        # Health check endpoint
        location /health {
            access_log off;
            return 200 "healthy\n";
            add_header Content-Type text/plain;
        }

        # Metrics endpoint (internal only)
        location /metrics {
            allow 172.29.0.0/16;
            deny all;

            proxy_pass http://127.0.0.1:9113/metrics;
        }

        # API v1 endpoints
        location /api/v1/ {
            limit_req zone=api burst=50 nodelay;

            # CORS headers
            add_header 'Access-Control-Allow-Origin' '$http_origin' always;
            add_header 'Access-Control-Allow-Methods' 'GET, POST, PUT, DELETE, OPTIONS' always;
            add_header 'Access-Control-Allow-Headers' 'DNT,User-Agent,X-Requested-With,If-Modified-Since,Cache-Control,Content-Type,Range,Authorization,X-Trace-ID' always;
            add_header 'Access-Control-Expose-Headers' 'Content-Length,Content-Range,X-Request-ID' always;

            if ($request_method = 'OPTIONS') {
                add_header 'Access-Control-Max-Age' 1728000;
                add_header 'Content-Type' 'text/plain; charset=utf-8';
                add_header 'Content-Length' 0;
                return 204;
            }

            # JWT validation (optional)
            # auth_jwt "API Access" token=$jwt_token;
            # auth_jwt_key_file /etc/nginx/jwt/public.key;

            # Route to specific backends
            location /api/v1/quantum/ {
                limit_req zone=quantum burst=20 nodelay;

                proxy_pass http://quantum_backend/;
                proxy_cache api_cache;
                proxy_cache_methods GET HEAD;
                proxy_cache_valid 200 1m;
                proxy_cache_use_stale error timeout updating http_500 http_502 http_503 http_504;
                proxy_cache_lock on;

                include /etc/nginx/proxy_params;
            }

            location /api/v1/economic/ {
                limit_req zone=economic burst=100 nodelay;

                proxy_pass http://economic_backend/;
                proxy_cache api_cache;
                proxy_cache_methods GET HEAD;
                proxy_cache_valid 200 10s;
                proxy_cache_bypass $http_cache_control;

                include /etc/nginx/proxy_params;
            }

            location /api/v1/ai/ {
                limit_req zone=ai burst=10 nodelay;

                proxy_pass http://ai_backend/;

                # Longer timeouts for AI operations
                proxy_connect_timeout 120s;
                proxy_send_timeout 120s;
                proxy_read_timeout 300s;

                include /etc/nginx/proxy_params;
            }

            location /api/v1/integration/ {
                proxy_pass http://integration_backend/;
                include /etc/nginx/proxy_params;
            }
        }

        # WebSocket endpoints
        location /ws/ {
