// End-to-End Integration Test: Quantum-Secured Economic Flow
// Tests the complete flow from quantum key generation to payment execution

use anyhow::Result;
use chrono::Utc;
use futures::StreamExt;
use reqwest::Client;
use rstest::*;
use serde::{Deserialize, Serialize};
use serial_test::serial;
use std::time::{Duration, Instant};
use tracing::{info, warn};
use uuid::Uuid;

#[derive(Debug, Serialize, Deserialize)]
struct QuantumKeyRequest {
    sender: String,
    receiver: String,
    security_level: u32,
}

#[derive(Debug, Serialize, Deserialize)]
struct QuantumKeyResponse {
    channel_id: String,
    public_key: String,
    entanglement_pairs: usize,
    #[serde(rename = "createdAt")]
    created_at: String,
}

#[derive(Debug, Serialize, Deserialize)]
struct PaymentRequest {
    channel_id: String,
    amount: u64,
    recipient: String,
    quantum_signature: bool,
}

#[derive(Debug, Serialize, Deserialize)]
struct PaymentResponse {
    transaction_id: String,
    success: bool,
    latency_ms: f64,
    quantum_verified: bool,
    #[serde(rename = "timestamp")]
    timestamp: String,
}

#[derive(Debug, Serialize, Deserialize)]
struct ChannelStatus {
    channel_id: String,
    balance: u64,
    nonce: u64,
    state: String,
}

struct TestContext {
    client: Client,
    base_url: String,
    quantum_url: String,
    economic_url: String,
    bridge_url: String,
}

impl TestContext {
    fn new() -> Self {
        let base_url = std::env::var("API_BASE_URL")
            .unwrap_or_else(|_| "http://localhost:8080".to_string());

        Self {
            client: Client::builder()
                .timeout(Duration::from_secs(30))
                .build()
                .expect("Failed to create HTTP client"),
            quantum_url: format!("{}/api/v1/quantum", base_url),
            economic_url: format!("{}/api/v1/economic", base_url),
            bridge_url: format!("{}/api/v1/integration/quantum-economic-bridge", base_url),
            base_url,
        }
    }

    async fn wait_for_services(&self) -> Result<()> {
        let mut attempts = 0;
        const MAX_ATTEMPTS: u32 = 30;

        while attempts < MAX_ATTEMPTS {
            let health_check = self.client
                .get(format!("{}/health", self.base_url))
                .send()
                .await;

            if health_check.is_ok() && health_check.unwrap().status().is_success() {
                info!("Services are healthy");
                return Ok(());
            }

            attempts += 1;
            tokio::time::sleep(Duration::from_secs(2)).await;
        }

        anyhow::bail!("Services did not become healthy after {} attempts", MAX_ATTEMPTS)
    }
}

#[fixture]
async fn context() -> TestContext {
    tracing_subscriber::fmt::init();

    let ctx = TestContext::new();
    ctx.wait_for_services().await.expect("Services should be healthy");
    ctx
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_quantum_key_generation(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing quantum key generation");

    // Generate quantum key for payment channel
    let key_request = QuantumKeyRequest {
        sender: "alice_address".to_string(),
        receiver: "bob_address".to_string(),
        security_level: 256,
    };

    let response = ctx.client
        .post(format!("{}/generate-key", ctx.quantum_url))
        .json(&key_request)
        .send()
        .await?;

    assert!(response.status().is_success(), "Key generation should succeed");

    let key_response: QuantumKeyResponse = response.json().await?;
    assert!(!key_response.channel_id.is_empty(), "Channel ID should be generated");
    assert!(!key_response.public_key.is_empty(), "Public key should be generated");
    assert!(key_response.entanglement_pairs >= 256, "Should have sufficient entangled pairs");

    info!("Quantum key generated: channel_id={}", key_response.channel_id);

    Ok(())
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_quantum_secured_payment(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing quantum-secured payment flow");

    // Step 1: Create quantum-secure channel
    let key_request = QuantumKeyRequest {
        sender: "test_sender".to_string(),
        receiver: "test_receiver".to_string(),
        security_level: 256,
    };

    let key_response = ctx.client
        .post(format!("{}/generate-key", ctx.quantum_url))
        .json(&key_request)
        .send()
        .await?
        .json::<QuantumKeyResponse>()
        .await?;

    let channel_id = key_response.channel_id;
    info!("Created channel: {}", channel_id);

    // Step 2: Execute quantum-secured payment
    let payment_request = PaymentRequest {
        channel_id: channel_id.clone(),
        amount: 1000,
        recipient: "test_receiver".to_string(),
        quantum_signature: true,
    };

    let start = Instant::now();
    let payment_response = ctx.client
        .post(format!("{}/transfer", ctx.bridge_url))
        .json(&payment_request)
        .send()
        .await?
        .json::<PaymentResponse>()
        .await?;
    let latency = start.elapsed();

    // Verify payment success
    assert!(payment_response.success, "Payment should succeed");
    assert!(payment_response.quantum_verified, "Payment should be quantum verified");
    assert!(!payment_response.transaction_id.is_empty(), "Transaction ID should be generated");

    // Check latency requirement (<50ms)
    assert!(
        latency.as_millis() < 50,
        "Payment latency should be under 50ms, was {}ms",
        latency.as_millis()
    );

    info!(
        "Payment completed: tx_id={}, latency={}ms",
        payment_response.transaction_id,
        latency.as_millis()
    );

    // Step 3: Verify channel state update
    let channel_status = ctx.client
        .get(format!("{}/channel/{}", ctx.economic_url, channel_id))
        .send()
        .await?
        .json::<ChannelStatus>()
        .await?;

    assert_eq!(channel_status.state, "active", "Channel should remain active");
    assert!(channel_status.nonce > 0, "Nonce should be incremented");

    Ok(())
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_payment_channel_lifecycle(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing complete payment channel lifecycle");

    // Create channel
    let key_request = QuantumKeyRequest {
        sender: "lifecycle_sender".to_string(),
        receiver: "lifecycle_receiver".to_string(),
        security_level: 128,
    };

    let channel = ctx.client
        .post(format!("{}/generate-key", ctx.quantum_url))
        .json(&key_request)
        .send()
        .await?
        .json::<QuantumKeyResponse>()
        .await?;

    let channel_id = channel.channel_id;

    // Execute multiple payments
    let payment_amounts = vec![100, 200, 300, 400, 500];
    let mut total_transferred = 0u64;

    for amount in payment_amounts {
        let payment = PaymentRequest {
            channel_id: channel_id.clone(),
            amount,
            recipient: "lifecycle_receiver".to_string(),
            quantum_signature: true,
        };

        let response = ctx.client
            .post(format!("{}/transfer", ctx.bridge_url))
            .json(&payment)
            .send()
            .await?
            .json::<PaymentResponse>()
            .await?;

        assert!(response.success, "Payment of {} should succeed", amount);
        total_transferred += amount;

        tokio::time::sleep(Duration::from_millis(100)).await;
    }

    // Verify final channel state
    let final_status = ctx.client
        .get(format!("{}/channel/{}", ctx.economic_url, channel_id))
        .send()
        .await?
        .json::<ChannelStatus>()
        .await?;

    info!(
        "Channel lifecycle complete: transferred={}, final_nonce={}",
        total_transferred, final_status.nonce
    );

    assert_eq!(final_status.nonce, 5, "Should have 5 transactions");

    // Close channel
    let close_response = ctx.client
        .post(format!("{}/channel/{}/close", ctx.economic_url, channel_id))
        .send()
        .await?;

    assert!(close_response.status().is_success(), "Channel close should succeed");

    Ok(())
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_concurrent_payments(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing concurrent payment processing");

    // Create multiple channels
    let num_channels = 5;
    let mut channels = Vec::new();

    for i in 0..num_channels {
        let key_request = QuantumKeyRequest {
            sender: format!("concurrent_sender_{}", i),
            receiver: format!("concurrent_receiver_{}", i),
            security_level: 128,
        };

        let channel = ctx.client
            .post(format!("{}/generate-key", ctx.quantum_url))
            .json(&key_request)
            .send()
            .await?
            .json::<QuantumKeyResponse>()
            .await?;

        channels.push(channel.channel_id);
    }

    // Execute concurrent payments
    let mut payment_futures = Vec::new();

    for (i, channel_id) in channels.iter().enumerate() {
        let client = ctx.client.clone();
        let bridge_url = ctx.bridge_url.clone();
        let channel_id = channel_id.clone();

        let future = tokio::spawn(async move {
            let payment = PaymentRequest {
                channel_id,
                amount: (i as u64 + 1) * 100,
                recipient: format!("concurrent_receiver_{}", i),
                quantum_signature: true,
            };

            let start = Instant::now();
            let result = client
                .post(format!("{}/transfer", bridge_url))
                .json(&payment)
                .send()
                .await?
                .json::<PaymentResponse>()
                .await;
            let latency = start.elapsed();

            Ok::<_, anyhow::Error>((result, latency))
        });

        payment_futures.push(future);
    }

    // Wait for all payments to complete
    let results = futures::future::join_all(payment_futures).await;

    let mut successful_payments = 0;
