// AI Agent Lifecycle Integration Tests
// Tests the complete lifecycle of autonomous AI agents from creation to retirement

use anyhow::Result;
use chrono::{DateTime, Duration, Utc};
use futures::StreamExt;
use reqwest::Client;
use rstest::*;
use serde::{Deserialize, Serialize};
use serial_test::serial;
use std::collections::HashMap;
use std::time::{Duration as StdDuration, Instant};
use tokio::time::timeout;
use tracing::{info, warn, error};
use uuid::Uuid;

#[derive(Debug, Serialize, Deserialize)]
struct AgentCreationRequest {
    agent_type: String,
    capabilities: Vec<String>,
    learning_objectives: Vec<String>,
    safety_constraints: SafetyConstraints,
    resource_limits: ResourceLimits,
}

#[derive(Debug, Serialize, Deserialize)]
struct SafetyConstraints {
    max_operations_per_minute: u32,
    allowed_domains: Vec<String>,
    forbidden_actions: Vec<String>,
    compliance_level: String,
    ethical_guidelines: Vec<String>,
}

#[derive(Debug, Serialize, Deserialize)]
struct ResourceLimits {
    max_memory_mb: u32,
    max_cpu_percent: u8,
    max_execution_time_seconds: u32,
    max_concurrent_tasks: u8,
}

#[derive(Debug, Serialize, Deserialize)]
struct AgentCreationResponse {
    agent_id: String,
    agent_type: String,
    status: String,
    capabilities: Vec<String>,
    #[serde(rename = "createdAt")]
    created_at: DateTime<Utc>,
    initial_state: AgentState,
}

#[derive(Debug, Serialize, Deserialize)]
struct AgentState {
    status: String,
    learning_progress: f32,
    task_queue_size: u32,
    memory_usage_mb: u32,
    cpu_usage_percent: f32,
    performance_metrics: PerformanceMetrics,
    safety_compliance: SafetyCompliance,
}

#[derive(Debug, Serialize, Deserialize)]
struct PerformanceMetrics {
    tasks_completed: u64,
    average_response_time_ms: f64,
    success_rate: f32,
    learning_rate: f32,
    adaptation_score: f32,
}

#[derive(Debug, Serialize, Deserialize)]
struct SafetyCompliance {
    compliance_score: f32,
    violations_count: u32,
    last_safety_check: DateTime<Utc>,
    ethical_alignment: f32,
}

#[derive(Debug, Serialize, Deserialize)]
struct TaskAssignmentRequest {
    agent_id: String,
    task_type: String,
    priority: u8,
    requirements: HashMap<String, String>,
    expected_duration_seconds: u32,
    quantum_enabled: bool,
}

#[derive(Debug, Serialize, Deserialize)]
struct TaskAssignmentResponse {
    task_id: String,
    assigned_at: DateTime<Utc>,
    estimated_completion: DateTime<Utc>,
    status: String,
}

#[derive(Debug, Serialize, Deserialize)]
struct TaskResult {
    task_id: String,
    status: String,
    result: serde_json::Value,
    execution_time_ms: f64,
    resource_usage: ResourceUsage,
    learning_gained: f32,
}

#[derive(Debug, Serialize, Deserialize)]
struct ResourceUsage {
    peak_memory_mb: u32,
    cpu_time_ms: u64,
    quantum_operations_count: u32,
    api_calls_made: u32,
}

#[derive(Debug, Serialize, Deserialize)]
struct AgentEvolutionRequest {
    agent_id: String,
    evolution_type: String,
    new_capabilities: Vec<String>,
    learning_data: serde_json::Value,
    validation_required: bool,
}

#[derive(Debug, Serialize, Deserialize)]
struct AgentEvolutionResponse {
    evolution_id: String,
    status: String,
    new_version: String,
    capabilities_added: Vec<String>,
    performance_change: f32,
}

struct TestContext {
    client: Client,
    base_url: String,
    ai_url: String,
    quantum_url: String,
    economic_url: String,
}

impl TestContext {
    fn new() -> Self {
        let base_url = std::env::var("API_BASE_URL")
            .unwrap_or_else(|_| "http://localhost:8080".to_string());

        Self {
            client: Client::builder()
                .timeout(StdDuration::from_secs(120))
                .build()
                .expect("Failed to create HTTP client"),
            ai_url: format!("{}/api/v1/ai", base_url),
            quantum_url: format!("{}/api/v1/quantum", base_url),
            economic_url: format!("{}/api/v1/economic", base_url),
            base_url,
        }
    }

    async fn wait_for_services(&self) -> Result<()> {
        let mut attempts = 0;
        const MAX_ATTEMPTS: u32 = 60;

        while attempts < MAX_ATTEMPTS {
            let health_check = self.client
                .get(format!("{}/health", self.base_url))
                .send()
                .await;

            if health_check.is_ok() && health_check.unwrap().status().is_success() {
                // Also check AI service health
                let ai_health = self.client
                    .get(format!("{}/health", self.ai_url))
                    .send()
                    .await;

                if ai_health.is_ok() && ai_health.unwrap().status().is_success() {
                    info!("All services are healthy");
                    return Ok(());
                }
            }

            attempts += 1;
            tokio::time::sleep(StdDuration::from_secs(2)).await;
        }

        anyhow::bail!("Services did not become healthy after {} attempts", MAX_ATTEMPTS)
    }

    async fn create_agent(&self, request: &AgentCreationRequest) -> Result<AgentCreationResponse> {
        let response = self.client
            .post(format!("{}/agents", self.ai_url))
            .json(request)
            .send()
            .await?;

        if !response.status().is_success() {
            let error_text = response.text().await?;
            anyhow::bail!("Agent creation failed: {}", error_text);
        }

        Ok(response.json().await?)
    }

    async fn get_agent_state(&self, agent_id: &str) -> Result<AgentState> {
        let response = self.client
            .get(format!("{}/agents/{}/state", self.ai_url, agent_id))
            .send()
            .await?;

        if !response.status().is_success() {
            anyhow::bail!("Failed to get agent state: {}", response.status());
        }

        Ok(response.json().await?)
    }

    async fn assign_task(&self, request: &TaskAssignmentRequest) -> Result<TaskAssignmentResponse> {
        let response = self.client
            .post(format!("{}/agents/{}/tasks", self.ai_url, request.agent_id))
            .json(request)
            .send()
            .await?;

        if !response.status().is_success() {
            anyhow::bail!("Task assignment failed: {}", response.status());
        }

        Ok(response.json().await?)
    }

    async fn wait_for_task_completion(&self, agent_id: &str, task_id: &str, timeout_seconds: u64) -> Result<TaskResult> {
        let start = Instant::now();
        let timeout_duration = StdDuration::from_secs(timeout_seconds);

        while start.elapsed() < timeout_duration {
            let response = self.client
                .get(format!("{}/agents/{}/tasks/{}", self.ai_url, agent_id, task_id))
                .send()
                .await?;

            if response.status().is_success() {
                let task_result: TaskResult = response.json().await?;

                if task_result.status == "completed" || task_result.status == "failed" {
                    return Ok(task_result);
                }
            }

            tokio::time::sleep(StdDuration::from_secs(2)).await;
        }

        anyhow::bail!("Task did not complete within {} seconds", timeout_seconds)
    }

    async fn evolve_agent(&self, request: &AgentEvolutionRequest) -> Result<AgentEvolutionResponse> {
        let response = self.client
            .post(format!("{}/agents/{}/evolve", self.ai_url, request.agent_id))
            .json(request)
            .send()
            .await?;

        if !response.status().is_success() {
            anyhow::bail!("Agent evolution failed: {}", response.status());
        }

        Ok(response.json().await?)
    }

    async fn retire_agent(&self, agent_id: &str, preserve_knowledge: bool) -> Result<()> {
        let response = self.client
            .delete(format!("{}/agents/{}", self.ai_url, agent_id))
            .query(&[("preserve_knowledge", preserve_knowledge)])
            .send()
            .await?;

        if !response.status().is_success() {
            anyhow::bail!("Agent retirement failed: {}", response.status());
        }

        Ok(())
    }
}

