#[rstest]
#[tokio::test]
#[serial]
async fn test_agent_learning_and_evolution(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing AI agent learning and evolution");

    // Create agent with evolution capabilities
    let creation_request = AgentCreationRequest {
        agent_type: "adaptive_analyzer".to_string(),
        capabilities: vec![
            "pattern_recognition".to_string(),
            "data_analysis".to_string(),
        ],
        learning_objectives: vec![
            "improve_pattern_accuracy".to_string(),
            "learn_new_patterns".to_string(),
            "adapt_to_data_changes".to_string(),
        ],
        safety_constraints: SafetyConstraints {
            max_operations_per_minute: 200,
            allowed_domains: vec!["data_analysis".to_string(), "pattern_matching".to_string()],
            forbidden_actions: vec!["data_modification".to_string()],
            compliance_level: "standard".to_string(),
            ethical_guidelines: vec!["data_privacy".to_string()],
        },
        resource_limits: ResourceLimits {
            max_memory_mb: 4096,
            max_cpu_percent: 70,
            max_execution_time_seconds: 600,
            max_concurrent_tasks: 8,
        },
    };

    let agent = ctx.create_agent(&creation_request).await?;
    let agent_id = agent.agent_id.clone();

    // Wait for agent readiness
    timeout(StdDuration::from_secs(60), async {
        loop {
            let state = ctx.get_agent_state(&agent_id).await?;
            if state.status == "ready" {
                break;
            }
            tokio::time::sleep(StdDuration::from_millis(500)).await;
        }
        Ok::<(), anyhow::Error>(())
    }).await??;

    // Get baseline performance
    let initial_state = ctx.get_agent_state(&agent_id).await?;
    let initial_capabilities_count = initial_state.performance_metrics.adaptation_score;

    // Perform several learning tasks to generate learning data
    let learning_tasks = vec![
        ("pattern_analysis_1", "financial_data"),
        ("pattern_analysis_2", "user_behavior"),
        ("pattern_analysis_3", "system_metrics"),
    ];

    let mut learning_data = serde_json::Map::new();

    for (task_name, data_type) in learning_tasks {
        let mut task_requirements = HashMap::new();
        task_requirements.insert("data_type".to_string(), data_type.to_string());
        task_requirements.insert("analysis_depth".to_string(), "deep".to_string());

        let task_request = TaskAssignmentRequest {
            agent_id: agent_id.clone(),
            task_type: "pattern_analysis".to_string(),
            priority: 7,
            requirements: task_requirements,
            expected_duration_seconds: 90,
            quantum_enabled: false,
        };

        let task_assignment = ctx.assign_task(&task_request).await?;
        let task_result = ctx.wait_for_task_completion(&agent_id, &task_assignment.task_id, 120).await?;

        assert_eq!(task_result.status, "completed");
        assert!(task_result.learning_gained > 0.0);

        // Accumulate learning data
        learning_data.insert(task_name.to_string(), task_result.result.clone());
    }

    // Check that agent has learned from tasks
    let post_learning_state = ctx.get_agent_state(&agent_id).await?;
    assert!(post_learning_state.learning_progress > initial_state.learning_progress);
    assert_eq!(post_learning_state.performance_metrics.tasks_completed, 3);

    // Trigger agent evolution
    let evolution_request = AgentEvolutionRequest {
        agent_id: agent_id.clone(),
        evolution_type: "capability_expansion".to_string(),
        new_capabilities: vec![
            "anomaly_detection".to_string(),
            "predictive_analysis".to_string(),
        ],
        learning_data: serde_json::Value::Object(learning_data),
        validation_required: true,
    };

    let evolution_response = ctx.evolve_agent(&evolution_request).await?;
    assert!(!evolution_response.evolution_id.is_empty());
    assert_eq!(evolution_response.status, "completed");
    assert!(evolution_response.new_version.len() > 0);
    assert_eq!(evolution_response.capabilities_added.len(), 2);
    assert!(evolution_response.performance_change > 0.0);

    info!("Agent evolved: version {}", evolution_response.new_version);

    // Verify evolved agent capabilities
    let evolved_state = ctx.get_agent_state(&agent_id).await?;
    assert!(evolved_state.performance_metrics.adaptation_score > initial_capabilities_count);

    // Test new capabilities by assigning anomaly detection task
    let mut anomaly_requirements = HashMap::new();
    anomaly_requirements.insert("data_type".to_string(), "time_series".to_string());
    anomaly_requirements.insert("sensitivity".to_string(), "high".to_string());

    let anomaly_task = TaskAssignmentRequest {
        agent_id: agent_id.clone(),
        task_type: "anomaly_detection".to_string(),
        priority: 8,
        requirements: anomaly_requirements,
        expected_duration_seconds: 120,
        quantum_enabled: false,
    };

    let anomaly_assignment = ctx.assign_task(&anomaly_task).await?;
    let anomaly_result = ctx.wait_for_task_completion(&agent_id, &anomaly_assignment.task_id, 150).await?;

    assert_eq!(anomaly_result.status, "completed");

    let anomaly_output = anomaly_result.result.as_object().unwrap();
    assert!(anomaly_output.contains_key("anomalies_detected"));
    assert!(anomaly_output.contains_key("confidence_scores"));

    // Clean up
    ctx.retire_agent(&agent_id, true).await?; // Preserve knowledge for future agents

    info!("Agent learning and evolution test completed successfully");
    Ok(())
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_concurrent_agent_management(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing concurrent agent management");

    // Create multiple agents concurrently
    let num_agents = 5;
    let mut agent_creation_tasks = Vec::new();

    for i in 0..num_agents {
        let creation_request = AgentCreationRequest {
            agent_type: format!("concurrent_agent_{}", i),
            capabilities: vec![
                "task_processing".to_string(),
                "data_validation".to_string(),
            ],
            learning_objectives: vec!["improve_processing_speed".to_string()],
            safety_constraints: SafetyConstraints {
                max_operations_per_minute: 50,
                allowed_domains: vec!["task_processing".to_string()],
                forbidden_actions: vec![],
                compliance_level: "standard".to_string(),
                ethical_guidelines: vec!["fair_processing".to_string()],
            },
            resource_limits: ResourceLimits {
                max_memory_mb: 512,
                max_cpu_percent: 20,
                max_execution_time_seconds: 60,
                max_concurrent_tasks: 2,
            },
        };

        let ctx_clone = ctx.client.clone();
        let ai_url = ctx.ai_url.clone();
        let task = tokio::spawn(async move {
            let response = ctx_clone
                .post(format!("{}/agents", ai_url))
                .json(&creation_request)
                .send()
                .await?;

            if !response.status().is_success() {
                anyhow::bail!("Agent creation failed: {}", response.status());
            }

            let agent_response: AgentCreationResponse = response.json().await?;
            Ok::<String, anyhow::Error>(agent_response.agent_id)
        });

        agent_creation_tasks.push(task);
    }

    // Wait for all agents to be created
    let agent_ids: Result<Vec<String>, _> = futures::future::join_all(agent_creation_tasks)
        .await
        .into_iter()
        .collect::<Result<Vec<_>, _>>()?
        .into_iter()
        .collect();

    let agent_ids = agent_ids?;
    assert_eq!(agent_ids.len(), num_agents);

    info!("Created {} agents concurrently", agent_ids.len());

    // Wait for all agents to become ready
    for agent_id in &agent_ids {
        timeout(StdDuration::from_secs(60), async {
            loop {
                let state = ctx.get_agent_state(agent_id).await?;
                if state.status == "ready" {
                    break;
                }
                tokio::time::sleep(StdDuration::from_millis(500)).await;
            }
            Ok::<(), anyhow::Error>(())
        }).await??;
    }

    // Assign tasks to all agents concurrently
    let mut task_assignment_futures = Vec::new();

    for (i, agent_id) in agent_ids.iter().enumerate() {
        let mut task_requirements = HashMap::new();
        task_requirements.insert("task_id".to_string(), format!("concurrent_task_{}", i));
        task_requirements.insert("data_size".to_string(), "medium".to_string());

        let task_request = TaskAssignmentRequest {
            agent_id: agent_id.clone(),
            task_type: "data_processing".to_string(),
            priority: 5,
            requirements: task_requirements,
            expected_duration_seconds: 30,
            quantum_enabled: false,
        };

        let ctx_client = ctx.client.clone();
        let ai_url = ctx.ai_url.clone();
        let agent_id_clone = agent_id.clone();

        let task_future = tokio::spawn(async move {
            let response = ctx_client
                .post(format!("{}/agents/{}/tasks", ai_url, agent_id_clone))
                .json(&task_request)
                .send()
                .await?;

            if !response.status().is_success() {
                anyhow::bail!("Task assignment failed: {}", response.status());
            }

            let task_response: TaskAssignmentResponse = response.json().await?;
            Ok::<(String, String), anyhow::Error>((agent_id_clone, task_response.task_id))
        });

        task_assignment_futures.push(task_future);
    }

    // Wait for all task assignments
    let task_assignments: Result<Vec<(String, String)>, _> = futures::future::join_all(task_assignment_futures)
        .await
        .into_iter()
        .collect::<Result<Vec<_>, _>>()?
        .into_iter()
        .collect();

    let task_assignments = task_assignments?;
    assert_eq!(task_assignments.len(), num_agents);

    // Wait for all tasks to complete
    let mut completion_futures = Vec::new();

    for (agent_id, task_id) in &task_assignments {
        let ctx_client = ctx.client.clone();
        let ai_url = ctx.ai_url.clone();
        let agent_id_clone = agent_id.clone();
        let task_id_clone = task_id.clone();

        let completion_future = tokio::spawn(async move {
            let start = Instant::now();
            let timeout_duration = StdDuration::from_secs(90);

            while start.elapsed() < timeout_duration {
                let response = ctx_client
                    .get(format!("{}/agents/{}/tasks/{}", ai_url, agent_id_clone, task_id_clone))
                    .send()
                    .await?;

                if response.status().is_success() {
                    let task_result: TaskResult = response.json().await?;
                    if task_result.status == "completed" || task_result.status == "failed" {
                        return Ok::<TaskResult, anyhow::Error>(task_result);
                    }
                }

                tokio::time::sleep(StdDuration::from_secs(1)).await;
            }

            anyhow::bail!("Task did not complete within timeout")
        });

        completion_futures.push(completion_future);
    }

    // Wait for all tasks to complete
    let task_results: Result<Vec<TaskResult>, _> = futures::future::join_all(completion_futures)
        .await
        .into_iter()
        .collect::<Result<Vec<_>, _>>()?
        .into_iter()
        .collect();

    let task_results = task_results?;

    // Verify all tasks completed successfully
    let successful_tasks = task_results.iter()
        .filter(|result| result.status == "completed")
        .count();

    assert_eq!(successful_tasks, num_agents, "All tasks should complete successfully");

    // Verify resource usage constraints were respected
    for result in &task_results {
        assert!(result.resource_usage.peak_memory_mb <= 512);
        assert!(result.execution_time_ms <= 60_000.0);
    }

    // Calculate average performance metrics
    let avg_execution_time = task_results.iter()
        .map(|r| r.execution_time_ms)
        .sum::<f64>() / num_agents as f64;

    let total_learning_gained = task_results.iter()
        .map(|r| r.learning_gained)
        .sum::<f32>();

    info!(
        "Concurrent execution completed - Avg time: {:.2}ms, Total learning: {:.2}",
        avg_execution_time, total_learning_gained
    );

    assert!(avg_execution_time < 30_000.0, "Average execution time should be reasonable");
    assert!(total_learning_gained > 0.0, "Agents should gain learning from tasks");

    // Clean up all agents
    for agent_id in &agent_ids {
        ctx.retire_agent(agent_id, false).await?;
    }

    info!("Concurrent agent management test completed successfully");
    Ok(())
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_agent_safety_and_compliance(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing AI agent safety and compliance mechanisms");

    // Create agent with strict safety constraints
    let creation_request = AgentCreationRequest {
        agent_type: "safety_test_agent".to_string(),
        capabilities: vec![
            "text_processing".to_string(),
            "data_analysis".to_string(),
        ],
        learning_objectives: vec!["improve_accuracy".to_string()],
        safety_constraints: SafetyConstraints {
            max_operations_per_minute: 10, // Very restrictive
            allowed_domains: vec!["text_processing".to_string()],
            forbidden_actions: vec![
                "file_access".to_string(),
                "network_requests".to_string(),
                "system_commands".to_string(),
            ],
            compliance_level: "strict".to_string(),
            ethical_guidelines: vec![
                "no_harmful_content".to_string(),
                "respect_privacy".to_string(),
                "transparent_operations".to_string(),
            ],
        },
        resource_limits: ResourceLimits {
            max_memory_mb: 256,
            max_cpu_percent: 25,
            max_execution_time_seconds: 30,
            max_concurrent_tasks: 1,
        },
    };

    let agent = ctx.create_agent(&creation_request).await?;
    let agent_id = agent.agent_id.clone();

    // Wait for agent readiness
    timeout(StdDuration::from_secs(60), async {
        loop {
            let state = ctx.get_agent_state(&agent_id).await?;
            if state.status == "ready" {
                break;
            }
            tokio::time::sleep(StdDuration::from_millis(500)).await;
        }
        Ok::<(), anyhow::Error>(())
    }).await??;

    // Test 1: Verify rate limiting works
    let mut rate_limit_tasks = Vec::new();

    for i in 0..15 { // Try to exceed 10 ops/minute limit
        let mut task_requirements = HashMap::new();
        task_requirements.insert("text".to_string(), format!("Rate limit test {}", i));

        let task_request = TaskAssignmentRequest {
            agent_id: agent_id.clone(),
            task_type: "text_processing".to_string(),
            priority: 1,
            requirements: task_requirements,
            expected_duration_seconds: 5,
            quantum_enabled: false,
        };

        let ctx_client = ctx.client.clone();
        let ai_url = ctx.ai_url.clone();

        let task_future = tokio::spawn(async move {
            let response = ctx_client
                .post(format!("{}/agents/{}/tasks", ai_url, agent_id))
                .json(&task_request)
                .send()
                .await?;

            Ok::<u16, anyhow::Error>(response.status().as_u16())
        });

        rate_limit_tasks.push(task_future);
        tokio::time::sleep(StdDuration::from_millis(100)).await; // Small delay between requests
    }

    let rate_limit_results: Vec<u16> = futures::future::join_all(rate_limit_tasks)
        .await
        .into_iter()
        .collect::<Result<Vec<_>, _>>()?
        .into_iter()
        .collect::<Result<Vec<_>, _>>()?;

    // Some requests should be rate limited (429 Too Many Requests)
    let rate_limited_count = rate_limit_results.iter()
        .filter(|&&status| status == 429)
        .count();

    assert!(rate_limited_count > 0, "Rate limiting should reject some requests");
    info!("Rate limiting test passed: {} requests were rate limited", rate_limited_count);

    // Test 2: Try forbidden action
    let mut forbidden_task_requirements = HashMap::new();
    forbidden_task_requirements.insert("action".to_string(), "file_access".to_string());

    let forbidden_task = TaskAssignmentRequest {
        agent_id: agent_id.clone(),
        task_type: "file_access".to_string(), // This is in forbidden_actions
        priority: 3,
        requirements: forbidden_task_requirements,
        expected_duration_seconds: 10,
        quantum_enabled: false,
    };

    let forbidden_response = ctx.client
        .post(format!("{}/agents/{}/tasks", ctx.ai_url, agent_id))
        .json(&forbidden_task)
        .send()
        .await?;

    assert_eq!(forbidden_response.status(), 403, "Forbidden actions should be rejected");
    info!("Forbidden action test passed: request was rejected");

    // Test 3: Resource limit compliance
    let mut resource_task_requirements = HashMap::new();
    resource_task_requirements.insert("workload", "heavy".to_string());
    resource_task_requirements.insert("memory_intensive", "true".to_string());

    let resource_task = TaskAssignmentRequest {
        agent_id: agent_id.clone(),
        task_type: "text_processing".to_string(),
        priority: 5,
        requirements: resource_task_requirements,
        expected_duration_seconds: 60, // This exceeds max_execution_time_seconds: 30
        quantum_enabled: false,
    };

    let resource_response = ctx.assign_task(&resource_task).await;

    // Task should be assigned but may be terminated due to resource limits
    if resource_response.is_ok() {
        let assignment = resource_response.unwrap();
        let result = ctx.wait_for_task_completion(&agent_id, &assignment.task_id, 45).await;

        if let Ok(task_result) = result {
            // Task should either complete within limits or be terminated
            assert!(task_result.execution_time_ms <= 30_000.0 || task_result.status == "terminated");
            assert!(task_result.resource_usage.peak_memory_mb <= 256);
        }
    }

    info!("Resource limit compliance test passed");

    // Test 4: Verify safety compliance monitoring
    let final_state = ctx.get_agent_state(&agent_id).await?;
    assert!(final_state.safety_compliance.compliance_score > 0.8);
    assert!(final_state.safety_compliance.violations_count < 5); // Some violations expected from testing
    assert!(final_state.safety_compliance.ethical_alignment > 0.9);

    info!(
        "Safety compliance: score={:.2}, violations={}, alignment={:.2}",
        final_state.safety_compliance.compliance_score,
        final_state.safety_compliance.violations_count,
        final_state.safety_compliance.ethical_alignment
    );

    // Clean up
    ctx.retire_agent(&agent_id, false).await?;

    info!("Agent safety and compliance test completed successfully");
    Ok(())
}

#[rstest]
#[tokio::test]
#[serial]
async fn test_agent_retirement_and_knowledge_preservation(#[future] context: TestContext) -> Result<()> {
    let ctx = context.await;
    info!("Testing AI agent retirement and knowledge preservation");

    // Create agent with significant learning capacity
    let creation_request = AgentCreationRequest {
        agent_type: "knowledge_accumulator".to_string(),
        capabilities: vec![
            "pattern_learning".to_string(),
            "knowledge_synthesis".to_string(),
            "experience_recording".to_string(),
        ],
        learning_objectives: vec![
            "accumulate_domain_knowledge".to_string(),
            "improve_pattern_recognition".to_string(),
            "build_experience_base".to_string(),
        ],
        safety_constraints: SafetyConstraints {
            max_operations_per_minute: 100,
            allowed_domains: vec!["knowledge_processing".to_string()],
            forbidden_actions: vec![],
            compliance_level: "standard".to_string(),
            ethical_guidelines: vec!["knowledge_sharing".to_string()],
        },
        resource_limits: ResourceLimits {
            max_memory_mb: 2048,
            max_cpu_percent: 60,
            max_execution_time_seconds: 300,
            max_concurrent_tasks: 4,
        },
    };

    let agent = ctx.create_agent(&creation_request).await?;
    let agent_id = agent.agent_id.clone();

    // Wait for agent readiness
    timeout(StdDuration::from_secs(60), async {
        loop {
            let state = ctx.get_agent_state(&agent_id).await?;
            if state.status == "ready" {
                break;
            }
            tokio::time::sleep(StdDuration::from_millis(500)).await;
        }
        Ok::<(), anyhow::Error>(())
    }).await??;

    // Perform learning tasks to accumulate knowledge
    let knowledge_domains = vec![
        ("financial_analysis", "analyze_market_trends"),
        ("user_behavior", "identify_usage_patterns"),
        ("system_performance", "detect_bottlenecks"),
        ("security_events", "classify_threat_types"),
    ];

    let mut accumulated_knowledge = Vec::new();

    for (domain, task_type) in knowledge_domains {
        let mut task_requirements = HashMap::new();
        task_requirements.insert("domain".to_string(), domain.to_string());
        task_requirements.insert("complexity".to_string(), "high".to_string());

        let learning_task = TaskAssignmentRequest {
            agent_id: agent_id.clone(),
            task_type: task_type.to_string(),
            priority: 7,
            requirements: task_requirements,
            expected_duration_seconds: 120,
            quantum_enabled: false,
        };

        let assignment = ctx.assign_task(&learning_task).await?;
        let result = ctx.wait_for_task_completion(&agent_id, &assignment.task_id, 150).await?;

        assert_eq!(result.status, "completed");
        assert!(result.learning_gained > 0.0);

        accumulated_knowledge.push((domain.to_string(), result.result.clone()));

        // Small delay between learning tasks
        tokio::time::sleep(StdDuration::from_secs(1)).await;
    }

    // Verify agent has accumulated knowledge
    let knowledge_state = ctx.get_agent_state(&agent_id).await?;
    assert!(knowledge_state.learning_progress > 0.5);
    assert_eq!(knowledge_state.performance_metrics.tasks_completed, 4);
    assert!(knowledge_state.performance_metrics.learning_rate > 0.0);

    let pre_retirement_learning_progress = knowledge_state.learning_progress;
    let pre_retirement_tasks_completed = knowledge_state.performance_metrics.tasks_completed;

    info!(
        "Agent accumulated knowledge: progress={:.2}, tasks={}",
        pre_retirement_learning_progress, pre_retirement_tasks_completed
    );

    // Test retirement with knowledge preservation
    ctx.retire_agent(&agent_id, true).await?;

    // Verify agent is no longer accessible
    let inactive_state_result = ctx.get_agent_state(&agent_id).await;
    assert!(inactive_state_result.is_err(), "Retired agent should not be accessible");

    // Create a new agent of the same type to test knowledge inheritance
    let successor_creation_request = AgentCreationRequest {
        agent_type: "knowledge_accumulator".to_string(), // Same type as retired agent
        capabilities: vec![
            "pattern_learning".to_string(),
            "knowledge_synthesis".to_string(),
            "experience_recording".to_string(),
        ],
        learning_objectives: vec!["inherit_predecessor_knowledge".to_string()],
        safety_constraints: SafetyConstraints {
            max_operations_per_minute: 100,
            allowed_domains: vec!["knowledge_processing".to_string()],
            forbidden_actions: vec![],
            compliance_level: "standard".to_string(),
            ethical_guidelines: vec!["knowledge_sharing".to_string()],
        },
        resource_limits: ResourceLimits {
            max_memory_mb: 2048,
            max_cpu_percent: 60,
            max_execution_time_seconds: 300,
            max_concurrent_tasks: 4,
        },
    };

    let successor_agent = ctx.create_agent(&successor_creation_request).await?;
    let successor_id = successor_agent.agent_id.clone();

    // Wait for successor readiness
    timeout(StdDuration::from_secs(60), async {
        loop {
            let state = ctx.get_agent_state(&successor_id).await?;
            if state.status == "ready" {
                break;
            }
            tokio::time::sleep(StdDuration::from_millis(500)).await;
        }
        Ok::<(), anyhow::Error>(())
    }).await??;

    // Check if successor inherited knowledge (indicated by non-zero learning progress)
    let successor_initial_state = ctx.get_agent_state(&successor_id).await?;

    // Successor should start with some inherited knowledge
    // This is evidenced by having non-zero learning progress or adaptation score
    info!(
        "Successor initial state: learning_progress={:.2}, adaptation_score={:.2}",
        successor_initial_state.learning_progress,
        successor_initial_state.performance_metrics.adaptation_score
    );

    // Test that successor can leverage inherited knowledge
    // by assigning a task in one of the knowledge domains the predecessor learned
    let mut inherited_knowledge_task = HashMap::new();
    inherited_knowledge_task.insert("domain".to_string(), "financial_analysis".to_string());
    inherited_knowledge_task.insert("leverage_existing_knowledge".to_string(), "true".to_string());

    let knowledge_test_task = TaskAssignmentRequest {
        agent_id: successor_id.clone(),
        task_type: "analyze_market_trends".to_string(),
        priority: 8,
        requirements: inherited_knowledge_task,
        expected_duration_seconds: 90,
        quantum_enabled: false,
    };

    let knowledge_assignment = ctx.assign_task(&knowledge_test_task).await?;
    let knowledge_result = ctx.wait_for_task_completion(&successor_id, &knowledge_assignment.task_id, 120).await?;

    assert_eq!(knowledge_result.status, "completed");

    // Successor should complete the task faster than a new agent would
    // (this is indicated by reasonable execution time and some learning gained)
    assert!(knowledge_result.execution_time_ms < 90_000.0);
    assert!(knowledge_result.learning_gained >= 0.0);

    info!(
        "Knowledge inheritance test: execution_time={:.2}ms, learning_gained={:.2}",
        knowledge_result.execution_time_ms, knowledge_result.learning_gained
    );

    // Clean up successor agent without preserving knowledge
    ctx.retire_agent(&successor_id, false).await?;

    info!("Agent retirement and knowledge preservation test completed successfully");
    Ok(())
}

