                .get(format!("{}/ai-economic-incentives/agent/{}/status", ctx.integration_url, economic_agent_id))
                .send()
                .await?
                .json::<serde_json::Value>()
                .await?;

            if agent_status["status"].as_str().unwrap() == "ready" &&
               agent_status["economic_account_active"].as_bool().unwrap() == true {
                break;
            }

            tokio::time::sleep(Duration::from_secs(1)).await;
        }
        Ok::<(), anyhow::Error>(())
    }).await??;

    // Step 2: Execute performance-based tasks and track metrics
    let performance_tasks = vec![
        ("market_analysis", "analyze_crypto_trends", 8),
        ("payment_optimization", "optimize_transaction_routes", 7),
        ("risk_assessment", "evaluate_investment_risks", 9),
