        // Wait for task completion
        let task_result = timeout(Duration::from_secs(120), async {
            loop {
                let status_response = ctx.client
                    .get(format!("{}/ai-economic-incentives/task/{}/status", ctx.integration_url, task_id))
                    .send()
                    .await?;

                if status_response.status().is_success() {
                    let task_status: serde_json::Value = status_response.json().await?;

                    if task_status["status"].as_str().unwrap() == "completed" {
                        return Ok::<serde_json::Value, anyhow::Error>(task_status);
                    }
                }

                tokio::time::sleep(Duration::from_secs(2)).await;
            }
        }).await??;

        let execution_time = task_start.elapsed();

        // Extract performance metrics
        let performance = PerformanceMetrics {
            task_completion_rate: task_result["completion_rate"].as_f64().unwrap() as f32,
            average_response_time_ms: execution_time.as_millis() as f64,
            accuracy_score: task_result["accuracy_score"].as_f64().unwrap() as f32,
            resource_efficiency: task_result["resource_efficiency"].as_f64().unwrap() as f32,
            learning_progress: task_result["learning_gained"].as_f64().unwrap() as f32,
