            performance.accuracy_score * 100.0,
            performance.resource_efficiency * 100.0,
            performance.average_response_time_ms
        );
    }

    // Step 3: Process AI performance incentives
    let total_performance_score = performance_data.iter()
        .map(|(_, metrics)| {
            (metrics.accuracy_score + metrics.resource_efficiency + metrics.task_completion_rate) / 3.0
        })
        .sum::<f32>() / performance_data.len() as f32;

    let base_reward = 1000u64; // Base reward amount
    let performance_multiplier = (total_performance_score * 2.0).min(2.0); // Cap at 2x multiplier
    let calculated_reward = (base_reward as f32 * performance_multiplier) as u64;

    let incentive = AIEconomicIncentive {
        incentive_id: Uuid::new_v4().to_string(),
        agent_id: economic_agent_id.clone(),
        performance_metrics: PerformanceMetrics {
            task_completion_rate: performance_data.iter().map(|(_, m)| m.task_completion_rate).sum::<f32>() / performance_data.len() as f32,
            average_response_time_ms: performance_data.iter().map(|(_, m)| m.average_response_time_ms).sum::<f64>() / performance_data.len() as f64,
            accuracy_score: performance_data.iter().map(|(_, m)| m.accuracy_score).sum::<f32>() / performance_data.len() as f32,
            resource_efficiency: performance_data.iter().map(|(_, m)| m.resource_efficiency).sum::<f32>() / performance_data.len() as f32,
            learning_progress: performance_data.iter().map(|(_, m)| m.learning_progress).sum::<f32>() / performance_data.len() as f32,
        },
        reward_amount: calculated_reward,
        payment_method: "quantum_secured_transfer".to_string(),
        quantum_secured: true,
    };

    let incentive_result = ctx.process_ai_economic_incentive(&incentive).await?;

    // Verify incentive processing
    assert_eq!(incentive_result["status"].as_str().unwrap(), "processed");
    assert_eq!(incentive_result["payment_status"].as_str().unwrap(), "completed");
    assert_eq!(incentive_result["reward_distributed"].as_u64().unwrap(), calculated_reward);
    assert_eq!(incentive_result["quantum_secured"].as_bool().unwrap(), true);

    // Check agent's economic account balance
    let account_balance = ctx.client
        .get(format!("{}/ai-economic-incentives/agent/{}/balance", ctx.integration_url, economic_agent_id))
        .send()
        .await?
        .json::<serde_json::Value>()
        .await?;

    assert!(account_balance["current_balance"].as_u64().unwrap() >= calculated_reward);
    assert_eq!(account_balance["currency"].as_str().unwrap(), "MANIFOLD");

    info!(
        "AI-Economic incentives processed: reward={} MANIFOLD, balance={} MANIFOLD, performance_score={:.2}%",
        calculated_reward,
        account_balance["current_balance"].as_u64().unwrap(),
        total_performance_score * 100.0
    );

    info!("AI-Economic integration test completed successfully");
    Ok(())
}

