#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    println!(" Manifolded Quantum Performance Benchmarking Suite");
    println!("======================================================");

    let results = QuantumBenchmarks::run_comprehensive_benchmark().await?;

    QuantumBenchmarks::print_results(&results);

    let filename = format!("manifolded_quantum_benchmark_{}.json", 
                          chrono::Utc::now().format("%Y%m%d_%H%M%S"));
    QuantumBenchmarks::save_results(&results, &filename).await?;

    // Performance thresholds check
    if results.overall_performance_score < 80.0 {
        println!("\n  Performance score below threshold (80). Review configuration and hardware.");
        std::process::exit(1);
    } else {
        println!("\n Performance benchmarks passed! System ready for production.");
    }

    Ok(())
}
